async def question(message: Message):
    """–û–±—Ä–∞–±–æ—Ç–∫–∞ –≤–æ–ø—Ä–æ—Å–∞ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è."""
    await message.answer("‚ÑπÔ∏è –û –±–æ—Ç–µ:\n–≠—Ç–æ—Ç –±–æ—Ç —Å–æ–∑–¥–∞–Ω, —á—Ç–æ–±—ã –ø–æ–º–æ—á—å –≤–∞–º —Ä–∞–∑–≤–∏–≤–∞—Ç—å –æ—Å–æ–∑–Ω–∞–Ω–Ω–æ—Å—Ç—å –∏ —É–ª—É—á—à–∞—Ç—å –∫–∞—á–µ—Å—Ç–≤–æ –∂–∏–∑–Ω–∏. –° –µ–≥–æ –ø–æ–º–æ—â—å—é –≤—ã —Å–º–æ–∂–µ—Ç–µ:\n\n–ú–µ–¥–∏—Ç–∏—Ä–æ–≤–∞—Ç—å –∏ –∑–∞–Ω–∏–º–∞—Ç—å—Å—è –¥—ã—Ö–∞—Ç–µ–ª—å–Ω—ã–º–∏ –ø—Ä–∞–∫—Ç–∏–∫–∞–º–∏ –¥–ª—è —É—Å–ø–æ–∫–æ–µ–Ω–∏—è —É–º–∞ –∏ –≤–æ—Å—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–∏—è –≤–Ω—É—Ç—Ä–µ–Ω–Ω–µ–π –≥–∞—Ä–º–æ–Ω–∏–∏ üßò‚Äç‚ôÄÔ∏è\n–ü–æ–ª—É—á–∞—Ç—å –º–æ—Ç–∏–≤–∏—Ä—É—é—â–∏–µ —Ü–∏—Ç–∞—Ç—ã, –∫–æ—Ç–æ—Ä—ã–µ –±—É–¥—É—Ç –≤–¥–æ—Ö–Ω–æ–≤–ª—è—Ç—å –≤–∞—Å –Ω–∞ –ª–∏—á–Ω–æ—Å—Ç–Ω—ã–π —Ä–æ—Å—Ç –∏ –ø–æ–∑–∏—Ç–∏–≤–Ω—ã–µ –∏–∑–º–µ–Ω–µ–Ω–∏—è ‚ú®\n–í–µ—Å—Ç–∏ –ª–∏—á–Ω—ã–π –¥–Ω–µ–≤–Ω–∏–∫, –≥–¥–µ –≤—ã —Å–º–æ–∂–µ—Ç–µ –æ—Ç—Å–ª–µ–∂–∏–≤–∞—Ç—å —Å–≤–æ–π –ø—Ä–æ–≥—Ä–µ—Å—Å, –∑–∞–ø–∏—Å—ã–≤–∞—Ç—å –º—ã—Å–ª–∏ –∏ —Ü–µ–ª–∏ üìù\n–°–æ–∑–¥–∞–Ω–æ —Å –ª—é–±–æ–≤—å—é –∏ –∑–∞–±–æ—Ç–æ–π –æ –≤–∞—à–µ–º –≤–Ω—É—Ç—Ä–µ–Ω–Ω–µ–º –º–∏—Ä–µ üíå\n\nüîß –û—Å–Ω–æ–≤–Ω—ã–µ –∫–æ–º–∞–Ω–¥—ã:\n\n/start ‚Äî –Ω–∞—á–Ω–∏—Ç–µ —Å–≤–æ–µ –ø—É—Ç–µ—à–µ—Å—Ç–≤–∏–µ —Å –±–æ—Ç–æ–º. –ó–∞–ø—É—Å—Ç–∏—Ç–µ –µ–≥–æ –∏ –Ω–∞—Å—Ç—Ä–æ–π—Ç–µ –ø–æ–¥ —Å–≤–æ–∏ —Ü–µ–ª–∏.\n/menu1 ‚Äî –æ—Ç–∫—Ä–æ—é—Ç—Å—è —Ä–∞–∑–ª–∏—á–Ω—ã–µ —Ñ—É–Ω–∫—Ü–∏–∏, –∫–æ—Ç–æ—Ä—ã–µ –ø–æ–º–æ–≥—É—Ç –≤–∞–º —Ä–∞–∑–≤–∏–≤–∞—Ç—å –æ—Å–æ–∑–Ω–∞–Ω–Ω–æ—Å—Ç—å. –ó–¥–µ—Å—å –≤—ã –Ω–∞–π–¥–µ—Ç–µ –º–µ–¥–∏—Ç–∞—Ü–∏–∏, –¥—ã—Ö–∞—Ç–µ–ª—å–Ω—ã–µ –ø—Ä–∞–∫—Ç–∏–∫–∏, –∞ —Ç–∞–∫–∂–µ —Å–æ–≤–µ—Ç—ã, –∫–æ—Ç–æ—Ä—ã–µ –ø–æ–º–æ–≥—É—Ç —Å–æ—Ö—Ä–∞–Ω–∏—Ç—å –º–æ—Ç–∏–≤–∞—Ü–∏—é –∏ —É–¥–µ—Ä–∂–∏–≤–∞—Ç—å —Ñ–æ–∫—É—Å –Ω–∞ –ø—Ä–æ—Ü–µ—Å—Å–µ.\n/menu2 ‚Äî –æ—Ç–∫—Ä–æ–µ—Ç—Å—è –≤–∞—à –ø–µ—Ä—Å–æ–Ω–∞–ª—å–Ω—ã–π –¥–Ω–µ–≤–Ω–∏–∫, –≥–¥–µ –≤—ã —Å–º–æ–∂–µ—Ç–µ –∑–∞–ø–∏—Å—ã–≤–∞—Ç—å —Å–≤–æ–π –ø—Ä–æ–≥—Ä–µ—Å—Å, —Å—Ç–∞–≤–∏—Ç—å —Ü–µ–ª–∏ –∏ —Ñ–∏–∫—Å–∏—Ä–æ–≤–∞—Ç—å –≤–∞–∂–Ω—ã–µ –º–æ–º–µ–Ω—Ç—ã –Ω–∞ –ø—É—Ç–∏ –∫ –≥–∞—Ä–º–æ–Ω–∏–∏.")
# –ö–û–ù–ï–¶ –ö–ù–û–ü–ö–ò 5 "‚ÑπÔ∏è –û –±–æ—Ç–µ"

# –û–¢–ó–´–í
# –ü—É—Ç—å –∫ —Ñ–∞–π–ª—É –¥–ª—è —Ö—Ä–∞–Ω–µ–Ω–∏—è –æ—Ç–∑—ã–≤–æ–≤
FEEDBACK_FILE = 'feedbacks.json'
class UserStates(StatesGroup):
    leaving_feedback = State()  # –°–æ—Å—Ç–æ—è–Ω–∏–µ –¥–ª—è –æ—Å—Ç–∞–≤–ª–µ–Ω–∏—è –æ—Ç–∑—ã–≤–∞
# –§—É–Ω–∫—Ü–∏—è –¥–ª—è –¥–æ–±–∞–≤–ª–µ–Ω–∏—è –æ—Ç–∑—ã–≤–∞ –≤ JSON
def add_to_json(file_path, data):
    """–î–æ–±–∞–≤–∏—Ç—å –æ—Ç–∑—ã–≤ –≤ JSON —Ñ–∞–π–ª"""
    try:
        # –ü—Ä–æ–≤–µ—Ä–∫–∞, —Å—É—â–µ—Å—Ç–≤—É–µ—Ç –ª–∏ —Ñ–∞–π–ª
        if os.path.exists(file_path):
            # –ï—Å–ª–∏ —Ñ–∞–π–ª —Å—É—â–µ—Å—Ç–≤—É–µ—Ç, –∑–∞–≥—Ä—É–∂–∞–µ–º —Å—Ç–∞—Ä—ã–µ –æ—Ç–∑—ã–≤—ã
            with open(file_path, 'r', encoding='utf-8') as file:
                feedbacks = json.load(file)
        else:
            # –ï—Å–ª–∏ —Ñ–∞–π–ª–∞ –Ω–µ—Ç, —Å–æ–∑–¥–∞–µ–º –Ω–æ–≤—ã–π —Å–ø–∏—Å–æ–∫ –¥–ª—è –æ—Ç–∑—ã–≤–æ–≤
            feedbacks = []
        # –î–æ–±–∞–≤–ª—è–µ–º –Ω–æ–≤—ã–π –æ—Ç–∑—ã–≤
        feedbacks.append(data)
        # –ó–∞–ø–∏—Å—ã–≤–∞–µ–º –æ–±–Ω–æ–≤–ª–µ–Ω–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ –æ–±—Ä–∞—Ç–Ω–æ –≤ —Ñ–∞–π–ª
        with open(file_path, 'w', encoding='utf-8') as file:
            json.dump(feedbacks, file, ensure_ascii=False, indent=4)
    except Exception as e:
        logging.error(f"Error while saving feedback to JSON: {e}")
        raise

# –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –∫–Ω–æ–ø–∫–∏ "–û—Å—Ç–∞–≤–∏—Ç—å –æ—Ç–∑—ã–≤"
@dp.message(F.text == "üí≠ –û—Å—Ç–∞–≤–∏—Ç—å –æ—Ç–∑—ã–≤")
async def feedback_prompt(message: types.Message, state: FSMContext):
    await state.set_state(UserStates.leaving_feedback)
    await message.answer(
        "–ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –Ω–∞–ø–∏—à–∏—Ç–µ –≤–∞—à –æ—Ç–∑—ã–≤ –∏–ª–∏ –ø—Ä–µ–¥–ª–æ–∂–µ–Ω–∏–µ:",
        reply_markup=types.ReplyKeyboardMarkup(
            keyboard=[[types.KeyboardButton(text="–û—Ç–º–µ–Ω–∞")]],
            resize_keyboard=True
        )
    )

# –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –¥–ª—è –ø–æ–ª—É—á–µ–Ω–∏—è –æ—Ç–∑—ã–≤–∞
@dp.message(UserStates.leaving_feedback)
async def process_feedback(message: types.Message, state: FSMContext):
    if message.text == "–û—Ç–º–µ–Ω–∞":
        await state.clear()
        await message.answer("–û—Ç–ø—Ä–∞–≤–∫–∞ –æ—Ç–∑—ã–≤–∞ –æ—Ç–º–µ–Ω–µ–Ω–∞.")
        await cmd_menu1(message)
        return

    try:
        feedback = message.text
        # –§–æ—Ä–º–∏—Ä—É–µ–º –¥–∞–Ω–Ω—ã–µ –¥–ª—è —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏—è
        data = {"user_id": str(message.from_user.id), "feedback": feedback}

        # –î–æ–±–∞–≤–ª—è–µ–º –æ—Ç–∑—ã–≤ –≤ JSON
        add_to_json(FEEDBACK_FILE, data)

        await message.answer(
            "–°–ø–∞—Å–∏–±–æ –±–æ–ª—å—à–æ–µ –∑–∞ –≤–∞—à –æ—Ç–∑—ã–≤! ü´∂\n"
            "–ú—ã —Ü–µ–Ω–∏–º –≤–∞—à–µ –º–Ω–µ–Ω–∏–µ –∏ –ø–æ—Å—Ç–∞—Ä–∞–µ–º—Å—è —Å—Ç–∞—Ç—å –ª—É—á—à–µ!",
        )
        # –í–æ–∑–≤—Ä–∞—â–∞–µ–º –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è –≤ –≥–ª–∞–≤–Ω–æ–µ –º–µ–Ω—é
        await cmd_menu1(message)

    except Exception as e:
        logging.error(f"Error saving feedback: {e}")
        await message.answer(
            "–ü—Ä–æ–∏–∑–æ—à–ª–∞ –æ—à–∏–±–∫–∞ –ø—Ä–∏ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏–∏ –æ—Ç–∑—ã–≤–∞. –ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –ø–æ–ø—Ä–æ–±—É–π—Ç–µ –ø–æ–∑–∂–µ.",
            await cmd_menu1(message)
        )
    finally:
        await state.clear()

# –ö–û–ù–ï–¶ –û–¢–ó–´–í–ê


# –î–ù–ï–í–ù–ò–ö + –¶–ï–õ–ò -- –ú–ï–ù–Æ
# –ü—É—Ç—å –∫ —Ñ–∞–π–ª–∞–º (–ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å—Å–∫–∏–π ID –±—É–¥–µ—Ç –∏—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å—Å—è –¥–ª—è —Å–æ–∑–¥–∞–Ω–∏—è —É–Ω–∏–∫–∞–ª—å–Ω—ã—Ö —Ñ–∞–π–ª–æ–≤)
user_diary_file = 'diary_{}.txt'
user_goals_file = 'goals_{}.json'

# –î–ù–ï–í–ù–ò–ö
# –§—É–Ω–∫—Ü–∏—è –¥–ª—è —Å–æ–∑–¥–∞–Ω–∏—è –Ω–æ–≤–æ–π –∑–∞–ø–∏—Å–∏ –≤ –¥–Ω–µ–≤–Ω–∏–∫
async def create_diary_entry(user_id: int, date: str, entry: str, micro_output: str):
    file_name = user_diary_file.format(user_id)
    entry_data = f"{date}\n{entry}\n–ú–∏–Ω–∏-–≤—ã–≤–æ–¥: {micro_output}\n\n"
    with open(file_name, 'a', encoding='utf-8') as file:
        file.write(entry_data)

def is_valid_date(date: str) -> bool:
    """
    –§—É–Ω–∫—Ü–∏—è –¥–ª—è –ø—Ä–æ–≤–µ—Ä–∫–∏ –∫–æ—Ä—Ä–µ–∫—Ç–Ω–æ—Å—Ç–∏ –¥–∞—Ç—ã (YYYY-MM-DD) —Å —É—á–µ—Ç–æ–º —Å—É—â–µ—Å—Ç–≤—É—é—â–∏—Ö –¥–Ω–µ–π –≤ –º–µ—Å—è—Ü–µ.
    """
    try:
        # –ü—Ä–µ–æ–±—Ä–∞–∑—É–µ–º —Å—Ç—Ä–æ–∫—É –≤ –æ–±—ä–µ–∫—Ç datetime, –µ—Å–ª–∏ –¥–∞—Ç–∞ —Å—É—â–µ—Å—Ç–≤—É–µ—Ç
        datetime.strptime(date, '%Y-%m-%d')
        return True
    except ValueError:
        # –ï—Å–ª–∏ –≤–æ–∑–Ω–∏–∫–∞–µ—Ç –æ—à–∏–±–∫–∞, –∑–Ω–∞—á–∏—Ç –¥–∞—Ç–∞ –Ω–µ–∫–æ—Ä—Ä–µ–∫—Ç–Ω–∞
        return False

class Form1(StatesGroup):
    waiting_for_date = State()  # –û–∂–∏–¥–∞–Ω–∏–µ –≤–≤–æ–¥–∞ –¥–∞—Ç—ã
    waiting_for_entry = State()  # –û–∂–∏–¥–∞–Ω–∏–µ –∑–∞–ø–∏—Å–∏
    waiting_for_micro_output = State()  # –û–∂–∏–¥–∞–Ω–∏–µ –º–∏–∫—Ä–æ-–≤—ã–≤–æ–¥–∞

# –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –¥–ª—è –∫–Ω–æ–ø–∫–∏ "–î–æ–±–∞–≤–∏—Ç—å –Ω–æ–≤—É—é –∑–∞–ø–∏—Å—å"
@dp.callback_query(F.data == 'content')
async def send_content(call: CallbackQuery, state: FSMContext):
    # –ù–∞—á–∏–Ω–∞–µ–º —Ä–∞–∑–≥–æ–≤–æ—Ä —Å –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–º
    await call.message.answer("–í–µ–¥—è —Å–≤–æ–π –¥–Ω–µ–≤–Ω–∏–∫, —Ç—ã –º–æ–∂–µ—à—å –∑–∞–ø–∏—Å—ã–≤–∞—Ç—å –¥–∞—Ç—É, –æ–ø–∏—Å–∞–Ω–∏–µ —Å–∏—Ç—É–∞—Ü–∏–π, —Å–≤–æ–∏ –º—ã—Å–ª–∏, —á—É–≤—Å—Ç–≤–∞ –∏ —ç–º–æ—Ü–∏–∏, –∫–æ—Ç–æ—Ä—ã–µ –≤–æ–∑–Ω–∏–∫–ª–∏ –≤ —ç—Ç–æ—Ç –º–æ–º–µ–Ω—Ç. –≠—Ç–æ –ø–æ–º–æ–∂–µ—Ç —Ç–µ–±–µ –ª—É—á—à–µ –ø–æ–Ω—è—Ç—å —Å–µ–±—è –∏ —Ç–æ, —á—Ç–æ –ø—Ä–æ–∏—Å—Ö–æ–¥–∏—Ç –≤–æ–∫—Ä—É–≥. –í –∫–æ–Ω—Ü–µ –∫–∞–∂–¥–æ–≥–æ –∑–∞–ø–∏—Å–∏ –æ—Å—Ç–∞–≤–ª—è–π –º–∏–Ω–∏-–≤—ã–≤–æ–¥ ‚Äî –ø–æ–¥—ã—Ç–æ–∂–∏–≤, —á—Ç–æ —Ç—ã –ø–æ—á—É–≤—Å—Ç–≤–æ–≤–∞–ª –∏–ª–∏ –ø–æ–Ω—è–ª. –ö–æ–Ω–µ—á–Ω–æ, –µ—Å–ª–∏ –≤—ã–≤–æ–¥–æ–≤ –Ω–µ—Ç, –º–æ–∂–Ω–æ –ø—Ä–æ—Å—Ç–æ –ø–æ—Å—Ç–∞–≤–∏—Ç—å –ø—Ä–æ—á–µ—Ä–∫. –ù–æ –ø–æ–º–Ω–∏, —á—Ç–æ –∞–Ω–∞–ª–∏–∑ —ç—Ç–∏—Ö –∑–∞–ø–∏—Å–µ–π –≤ –±—É–¥—É—â–µ–º –±—É–¥–µ—Ç –ø–æ–ª–µ–∑–µ–Ω –¥–ª—è —Ç–≤–æ–µ–≥–æ –ª–∏—á–Ω–æ–≥–æ —Ä–æ—Å—Ç–∞ –∏ –æ—Å–æ–∑–Ω–∞–Ω–Ω–æ—Å—Ç–∏. –ú—ã —Å—Ç—Ä–µ–º–∏–º—Å—è –∫ –ª—É—á—à–µ–º—É –ø–æ–Ω–∏–º–∞–Ω–∏—é —Å–µ–±—è, –∏ –¥–∞–∂–µ —Å–∞–º—ã–µ –º–∞–ª–µ–Ω—å–∫–∏–µ –≤—ã–≤–æ–¥—ã –º–æ–≥—É—Ç —Å—Ç–∞—Ç—å –∫–ª—é—á–æ–º –∫ –≤–∞–∂–Ω—ã–º –∏–Ω—Å–∞–π—Ç–∞–º üå±\n\nüìÜ –í–≤–µ–¥–∏—Ç–µ –¥–∞—Ç—É –∑–∞–ø–∏—Å–∏ (–Ω–∞–ø—Ä–∏–º–µ—Ä, 2025-01-20):")
    await state.set_state(Form1.waiting_for_date)

# –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –¥–ª—è –≤–≤–æ–¥–∞ –¥–∞—Ç—ã –∑–∞–ø–∏—Å–∏
@dp.message(State("waiting_for_date"))
async def process_date(message: Message, state: FSMContext):
    date = message.text

    # –ü—Ä–æ–≤–µ—Ä–∫–∞ –Ω–∞ –∫–æ—Ä—Ä–µ–∫—Ç–Ω–æ—Å—Ç—å –¥–∞—Ç—ã
    if is_valid_date(date):
        await state.update_data(date=date)
        # –ü–µ—Ä–µ—Ö–æ–¥–∏–º –∫ –≤–≤–æ–¥—É –∑–∞–ø–∏—Å–∏
        await message.answer("üìù –í–≤–µ–¥–∏—Ç–µ –≤–∞—à—É –∑–∞–ø–∏—Å—å –¥–ª—è –¥–Ω–µ–≤–Ω–∏–∫–∞:")
        await state.set_state(Form1.waiting_for_entry)
    else:
        # –ï—Å–ª–∏ –¥–∞—Ç–∞ –Ω–µ–∫–æ—Ä—Ä–µ–∫—Ç–Ω–∞—è, –∑–∞–ø—Ä–∞—à–∏–≤–∞–µ–º –≤–≤–æ–¥ —Å–Ω–æ–≤–∞
        await message.answer(
            "‚ùå –ù–µ–∫–æ—Ä—Ä–µ–∫—Ç–Ω—ã–π —Ñ–æ—Ä–º–∞—Ç –¥–∞—Ç—ã. –ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –≤–≤–µ–¥–∏—Ç–µ –¥–∞—Ç—É –≤ —Ñ–æ—Ä–º–∞—Ç–µ 'YYYY-MM-DD', –Ω–∞–ø—Ä–∏–º–µ—Ä, '2025-01-20'. –ü–æ–ø—Ä–æ–±—É–π—Ç–µ —Å–Ω–æ–≤–∞:"
        )

# –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –¥–ª—è –≤–≤–æ–¥–∞ —Å–∞–º–æ–π –∑–∞–ø–∏—Å–∏
@dp.message(State("waiting_for_entry"))
async def process_entry(message: Message, state: FSMContext):
    entry = message.text
    data = await state.get_data()
    date = data.get("date")
    await state.update_data(entry=entry)
    # –ü–µ—Ä–µ—Ö–æ–¥–∏–º –∫ –≤–≤–æ–¥—É –º–∏–∫—Ä–æ-–≤—ã–≤–æ–¥–∞
    await message.answer("üîª –í–≤–µ–¥–∏—Ç–µ –≤–∞—à –º–∏–Ω–∏-–≤—ã–≤–æ–¥ –¥–ª—è –∑–∞–ø–∏—Å–∏:")
    await state.set_state(Form1.waiting_for_micro_output)

# –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –¥–ª—è –≤–≤–æ–¥–∞ –º–∏–Ω–∏-–≤—ã–≤–æ–¥–∞
@dp.message(State("waiting_for_micro_output"))
async def process_micro_output(message: Message, state: FSMContext):
    micro_output = message.text
    data = await state.get_data()
    date = data.get("date")
    entry = data.get("entry")

    # –°–æ—Ö—Ä–∞–Ω—è–µ–º –∑–∞–ø–∏—Å—å –≤ –¥–Ω–µ–≤–Ω–∏–∫
    await create_diary_entry(message.from_user.id, date, entry, micro_output)
    await message.answer("–í–∞—à–∞ –∑–∞–ø–∏—Å—å —É—Å–ø–µ—à–Ω–æ –¥–æ–±–∞–≤–ª–µ–Ω–∞! üíå")
    await state.clear()

# –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –¥–ª—è –∫–Ω–æ–ø–∫–∏ "–ü—Ä–æ—Å–º–æ—Ç—Ä –ø—Ä–µ–¥—ã–¥—É—â–∏—Ö –∑–∞–ø–∏—Å–µ–π"
@dp.callback_query(F.data == 'all')
async def view_previous_entries(call: CallbackQuery):
    user_id = call.from_user.id
    file_name = user_diary_file.format(user_id)

    if os.path.exists(file_name):
        with open(file_name, 'r', encoding='utf-8') as file:
            content = file.read()
            if content:
                await call.message.answer(content)
            else:
                await call.message.answer("–£ –≤–∞—Å –Ω–µ—Ç –∑–∞–ø–∏—Å–µ–π –≤ –¥–Ω–µ–≤–Ω–∏–∫–µ.")
    else:
        await call.message.answer("–£ –≤–∞—Å –µ—â–µ –Ω–µ—Ç –∑–∞–ø–∏—Å–µ–π –≤ –¥–Ω–µ–≤–Ω–∏–∫–µ.")

dp.message.register(process_date, StateFilter(Form1.waiting_for_date))
dp.message.register(process_entry, StateFilter(Form1.waiting_for_entry))
dp.message.register(process_micro_output, StateFilter(Form1.waiting_for_micro_output))
# –ö–û–ù–ï–¶ –î–ù–ï–í–ù–ò–ö–ê

# –¶–ï–õ–ò
class Form2(StatesGroup):
    waiting_for_date = State()
    waiting_for_goal = State()
    waiting_for_goal_description = State()
    waiting_for_end_date = State()

# –§—É–Ω–∫—Ü–∏—è –¥–ª—è –∑–∞–ø–∏—Å–∏ —Ü–µ–ª–∏
async def create_goal(user_id: int, start_date: str, goal: str, description: str, end_date: str):
    file_name = user_goals_file.format(user_id)
    goal_data = {
        'start_date': start_date,
        'name': goal,
        'description': description,
        'end_date': end_date,
        'status': '–Ω–µ –≤—ã–ø–æ–ª–Ω–µ–Ω–æ'  # –ø–æ–ª–µ –¥–ª—è –æ—Ç—Å–ª–µ–∂–∏–≤–∞–Ω–∏—è —Å—Ç–∞—Ç—É—Å–∞ —Ü–µ–ª–∏
    }
    if os.path.exists(file_name):
        with open(file_name, 'r+', encoding='utf-8') as file:
            goals = json.load(file)
            goals.append(goal_data)
            file.seek(0)
            json.dump(goals, file, ensure_ascii=False, indent=4)
    else:
        with open(file_name, 'w', encoding='utf-8') as file:
            json.dump([goal_data], file, ensure_ascii=False, indent=4)

# –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –¥–ª—è –∫–Ω–æ–ø–∫–∏ "–ó–∞–ø–∏—Å–∞—Ç—å —Ü–µ–ª—å"
@dp.callback_query(F.data == 'goal')
async def add_goal(call: CallbackQuery, state: FSMContext):
    """–ó–∞–ø—Ä–æ—Å–∏—Ç—å –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—é –¥–ª—è –¥–æ–±–∞–≤–ª–µ–Ω–∏—è —Ü–µ–ª–∏"""
    await call.message.answer("–¶–µ–ª–∏ —Ñ–∏–∫—Å–∏—Ä—É—é—Ç—Å—è –≤ —Ñ–æ—Ä–º–∞—Ç–µ: –¥–∞—Ç–∞ –Ω–∞—á–∞–ª–∞, –Ω–∞–∑–≤–∞–Ω–∏–µ —Ü–µ–ª–∏, –æ–ø–∏—Å–∞–Ω–∏–µ —Ü–µ–ª–∏ –∏ –¥–∞—Ç–∞ –∑–∞–≤–µ—Ä—à–µ–Ω–∏—è (–∏–ª–∏ –±–µ—Å—Å—Ä–æ—á–Ω–æ). –¢–∞–∫–æ–π –ø–æ–¥—Ö–æ–¥ –ø–æ–º–æ–≥–∞–µ—Ç —è—Å–Ω–æ –≤–∏–¥–µ—Ç—å —Å–≤–æ–∏ –Ω–∞–º–µ—Ä–µ–Ω–∏—è –∏ —à–∞–≥–∏ –Ω–∞ –ø—É—Ç–∏ –∫ –Ω–∏–º. –°—Ç–∞–≤—è –ø–µ—Ä–µ–¥ —Å–æ–±–æ–π —Ü–µ–ª–∏, —Ç—ã –Ω–µ —Ç–æ–ª—å–∫–æ —Å—Ç–∞–Ω–æ–≤–∏—à—å—Å—è –±–æ–ª–µ–µ –æ—Å–æ–∑–Ω–∞–Ω–Ω—ã–º, –Ω–æ –∏ –Ω–∞—á–∏–Ω–∞–µ—à—å –æ—Ç—Å–ª–µ–∂–∏–≤–∞—Ç—å —Å–≤–æ–π –ø—Ä–æ–≥—Ä–µ—Å—Å. –≠—Ç–æ –≤–∞–∂–Ω–∞—è —á–∞—Å—Ç—å –≤–∑—Ä–æ—Å–ª–µ–Ω–∏—è ‚Äî —É—á–∏—Ç—å—Å—è –ø–ª–∞–Ω–∏—Ä–æ–≤–∞—Ç—å —Å–≤–æ—é –∂–∏–∑–Ω—å, —Å—Ç–∞–≤–∏—Ç—å –∞–º–±–∏—Ü–∏–æ–∑–Ω—ã–µ –∑–∞–¥–∞—á–∏ –∏ —É–≤–µ—Ä–µ–Ω–Ω–æ –¥–≤–∏–≥–∞—Ç—å—Å—è –∫ –Ω–∏–º. –ò –ø—É—Å—Ç—å –∫–∞–∂–¥–∞—è —Ü–µ–ª—å, –¥–∞–∂–µ —Å–∞–º–∞—è –º–∞–ª–µ–Ω—å–∫–∞—è, –±—É–¥–µ—Ç —Ç–≤–æ–∏–º —à–∞–≥–æ–º –∫ –Ω–µ–±—É! üåü\n\nüóì –í–≤–µ–¥–∏—Ç–µ –¥–∞—Ç—É –Ω–∞—á–∞–ª–∞ —Ü–µ–ª–∏ (–Ω–∞–ø—Ä–∏–º–µ—Ä, 2025-01-20):")
    await state.set_state(Form2.waiting_for_date)

# –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –¥–ª—è –≤–≤–æ–¥–∞ –¥–∞—Ç—ã –Ω–∞—á–∞–ª–∞ —Ü–µ–ª–∏
@dp.message(State("waiting_for_goal_start_date"))
async def process_goal_start_date(message: Message, state: FSMContext):
    start_date = message.text

    # –ü—Ä–æ–≤–µ—Ä–∫–∞ –Ω–∞ –∫–æ—Ä—Ä–µ–∫—Ç–Ω–æ—Å—Ç—å –¥–∞—Ç—ã
    if is_valid_date(start_date):
        await state.update_data(start_date=start_date)
        await message.answer("–î–∞—Ç–∞ –Ω–∞—á–∞–ª–∞ —Ü–µ–ª–∏ —É—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–∞.")

        # –ü–µ—Ä–µ—Ö–æ–¥–∏–º –∫ –≤–≤–æ–¥—É —Ü–µ–ª–∏
        await message.answer("üéØ –í–≤–µ–¥–∏—Ç–µ –Ω–∞–∑–≤–∞–Ω–∏–µ –≤–∞—à–µ–π —Ü–µ–ª–∏:")
        await state.set_state(Form2.waiting_for_goal)
    else:
        # –ï—Å–ª–∏ –¥–∞—Ç–∞ –Ω–µ–∫–æ—Ä—Ä–µ–∫—Ç–Ω–∞—è, –∑–∞–ø—Ä–∞—à–∏–≤–∞–µ–º –≤–≤–æ–¥ —Å–Ω–æ–≤–∞
        await message.answer(
            "‚ùå –ù–µ–∫–æ—Ä—Ä–µ–∫—Ç–Ω—ã–π —Ñ–æ—Ä–º–∞—Ç –¥–∞—Ç—ã. –ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –≤–≤–µ–¥–∏—Ç–µ –¥–∞—Ç—É –≤ —Ñ–æ—Ä–º–∞—Ç–µ 'YYYY-MM-DD', –Ω–∞–ø—Ä–∏–º–µ—Ä, '2025-01-20'. –ü–æ–ø—Ä–æ–±—É–π—Ç–µ —Å–Ω–æ–≤–∞:"
        )

# –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –¥–ª—è –≤–≤–æ–¥–∞ —Ü–µ–ª–∏
@dp.message(State("waiting_for_goal"))
async def process_goal(message: Message, state: FSMContext):
    goal = message.text
    await state.update_data(name=goal)

    # –ü–µ—Ä–µ—Ö–æ–¥–∏–º –∫ –≤–≤–æ–¥—É –æ–ø–∏—Å–∞–Ω–∏—è —Ü–µ–ª–∏
    await message.answer("üìñ –í–≤–µ–¥–∏—Ç–µ –æ–ø–∏—Å–∞–Ω–∏–µ –≤–∞—à–µ–π —Ü–µ–ª–∏:")
    await state.set_state(Form2.waiting_for_goal_description)

# –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –¥–ª—è –≤–≤–æ–¥–∞ –æ–ø–∏—Å–∞–Ω–∏—è —Ü–µ–ª–∏
@dp.message(State("waiting_for_goal_description"))
async def process_goal_description(message: Message, state: FSMContext):
    description = message.text
    await state.update_data(description=description)

    # –ü–µ—Ä–µ—Ö–æ–¥–∏–º –∫ –≤–≤–æ–¥—É –¥–∞—Ç—ã –∑–∞–≤–µ—Ä—à–µ–Ω–∏—è
    await message.answer("\nüìÜ –í–≤–µ–¥–∏—Ç–µ –¥–∞—Ç—É –∑–∞–≤–µ—Ä—à–µ–Ω–∏—è —Ü–µ–ª–∏ (–Ω–∞–ø—Ä–∏–º–µ—Ä, 2025-12-31), –∏–ª–∏ –Ω–∞–ø–∏—à–∏—Ç–µ '–±–µ—Å—Å—Ä–æ—á–Ω–æ':")
    await state.set_state(Form2.waiting_for_end_date)

# –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –¥–ª—è –≤–≤–æ–¥–∞ –¥–∞—Ç—ã –∑–∞–≤–µ—Ä—à–µ–Ω–∏—è —Ü–µ–ª–∏
@dp.message(State("waiting_for_goal_end_date"))
async def process_goal_end_date(message: Message, state: FSMContext):
    end_date = message.text
    # –ü—Ä–æ–≤–µ—Ä–∫–∞ –Ω–∞ "–±–µ—Å—Å—Ä–æ—á–Ω–æ"
    if end_date.lower() == "–±–µ—Å—Å—Ä–æ—á–Ω–æ":
        await state.update_data(end_date=end_date)
        await message.answer("–¶–µ–ª—å –±—É–¥–µ—Ç –±–µ—Å—Å—Ä–æ—á–Ω–æ–π.")
        end_date = '–ë–µ—Å—Å—Ä–æ—á–Ω–æ'
    # –ü—Ä–æ–≤–µ—Ä–∫–∞ –Ω–∞ –ø—Ä–∞–≤–∏–ª—å–Ω–æ—Å—Ç—å —Ñ–æ—Ä–º–∞—Ç–∞ –¥–∞—Ç—ã
    elif is_valid_date(end_date):
        await state.update_data(end_date=end_date)
        await message.answer(f"–î–∞—Ç–∞ –∑–∞–≤–µ—Ä—à–µ–Ω–∏—è —Ü–µ–ª–∏ —É—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–∞ –Ω–∞ {end_date}.")
    else:
        await message.answer(
            "‚ùå –ù–µ–∫–æ—Ä—Ä–µ–∫—Ç–Ω—ã–π —Ñ–æ—Ä–º–∞—Ç –¥–∞—Ç—ã. –ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –≤–≤–µ–¥–∏—Ç–µ –¥–∞—Ç—É –≤ —Ñ–æ—Ä–º–∞—Ç–µ 'YYYY-MM-DD' –∏–ª–∏ –Ω–∞–ø–∏—à–∏—Ç–µ '–±–µ—Å—Å—Ä–æ—á–Ω–æ'.")
        return

    # –ü–æ–ª—É—á–∞–µ–º –≤—Å–µ –¥–∞–Ω–Ω—ã–µ, —Å–æ–±—Ä–∞–Ω–Ω—ã–µ —Ä–∞–Ω–µ–µ
    data = await state.get_data()
    start_date = data.get("start_date")
    goal = data.get("name")
    description = data.get("description")

    # –°–æ—Ö—Ä–∞–Ω—è–µ–º —Ü–µ–ª—å
    await create_goal(message.from_user.id, start_date, goal, description, end_date)
    await message.answer("–í–∞—à–∞ —Ü–µ–ª—å —É—Å–ø–µ—à–Ω–æ –∑–∞–ø–∏—Å–∞–Ω–∞! üíå")
    await state.clear()

dp.message.register(process_goal_start_date, StateFilter(Form2.waiting_for_date))
dp.message.register(process_goal, StateFilter(Form2.waiting_for_goal))
dp.message.register(process_goal_description, StateFilter(Form2.waiting_for_goal_description))
dp.message.register(process_goal_end_date, StateFilter(Form2.waiting_for_end_date))


# –ó–∞–≥—Ä—É–∑–∏—Ç—å —Ü–µ–ª–∏ –∏–∑ —Ñ–∞–π–ª–∞
def load_goals_from_file(user_id):
    """–ó–∞–≥—Ä—É–∑–∫–∞ —Ü–µ–ª–µ–π –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è –∏–∑ —Ñ–∞–π–ª–∞"""
    file_name = f'goals_{user_id}.json'  # –ò–º—è —Ñ–∞–π–ª–∞ —Å —Ü–µ–ª—è–º–∏
    if os.path.exists(file_name):
        with open(file_name, 'r', encoding='utf-8') as file:
            try:
                goals = json.load(file)
                # –ü—Ä–æ–≤–µ—Ä–∫–∞, —á—Ç–æ –∫–∞–∂–¥—ã–π —ç–ª–µ–º–µ–Ω—Ç —Å–ø–∏—Å–∫–∞ —Ü–µ–ª–µ–π –∏–º–µ–µ—Ç –∫–ª—é—á 'name'
                for goal in goals:
                    if 'name' not in goal:
                        raise ValueError(f"–¶–µ–ª—å {goal} –Ω–µ —Å–æ–¥–µ—Ä–∂–∏—Ç –∫–ª—é—á–∞ 'name'.")
                return goals
            except json.JSONDecodeError:
                print(f"–û—à–∏–±–∫–∞ –ø—Ä–∏ —á—Ç–µ–Ω–∏–∏ JSON –∏–∑ —Ñ–∞–π–ª–∞ {file_name}.")
            except ValueError as e:
                print(e)
    return []  # –ï—Å–ª–∏ —Ñ–∞–π–ª –Ω–µ —Å—É—â–µ—Å—Ç–≤—É–µ—Ç –∏–ª–∏ –ø—Ä–æ–∏–∑–æ—à–ª–∞ –æ—à–∏–±–∫–∞, –≤–æ–∑–≤—Ä–∞—â–∞–µ–º –ø—É—Å—Ç–æ–π —Å–ø–∏—Å–æ–∫

# –°—Ç–µ–π—Ç—ã FSM
class GoalStates(StatesGroup):
    waiting_for_goal = State()  # –û–∂–∏–¥–∞–Ω–∏–µ –≤—ã–±–æ—Ä–∞ —Ü–µ–ª–∏
    waiting_for_time = State()  # –û–∂–∏–¥–∞–Ω–∏–µ –≤—Ä–µ–º–µ–Ω–∏ –Ω–∞–ø–æ–º–∏–Ω–∞–Ω–∏—è

# –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –∫–Ω–æ–ø–∫–∏ "‚òÑÔ∏è –ù–∞–ø–æ–º–∏–Ω–∞–Ω–∏–µ –æ —Ü–µ–ª–∏"
@dp.message(lambda message: message.text == "‚òÑÔ∏è –ù–∞–ø–æ–º–∏–Ω–∞–Ω–∏–µ –æ —Ü–µ–ª–∏")
async def set_reminder_prompt(message: types.Message, state: FSMContext):
    user_id = str(message.from_user.id)
    goals = load_goals_from_file(user_id)
    if not goals:
        await message.answer("–£ –≤–∞—Å –ø–æ–∫–∞ –Ω–µ—Ç —Ü–µ–ª–µ–π –¥–ª—è –Ω–∞–ø–æ–º–∏–Ω–∞–Ω–∏—è.", reply_markup=get_main_keyboard())
        return
    # –°–æ–∑–¥–∞–µ–º –∫–ª–∞–≤–∏–∞—Ç—É—Ä—É —Å —Ü–µ–ª—è–º–∏
    builder = ReplyKeyboardBuilder()
    for goal in goals:
        goal_name = goal.get("name") or "–ë–µ–∑ –Ω–∞–∑–≤–∞–Ω–∏—è"
        builder.add(KeyboardButton(text=goal_name))
    builder.add(KeyboardButton(text="–û—Ç–º–µ–Ω–∞"))
    keyboard = builder.as_markup(resize_keyboard=True)
    await message.answer("–í—ã–±–µ—Ä–∏—Ç–µ —Ü–µ–ª—å –¥–ª—è —É—Å—Ç–∞–Ω–æ–≤–∫–∏ –Ω–∞–ø–æ–º–∏–Ω–∞–Ω–∏—è:", reply_markup=keyboard)
    await state.set_state(GoalStates.waiting_for_goal)


# –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –≤—Ä–µ–º–µ–Ω–∏ –Ω–∞–ø–æ–º–∏–Ω–∞–Ω–∏—è
@dp.message(StateFilter(GoalStates.waiting_for_time))
async def process_reminder_time(message: types.Message, state: FSMContext):
    try:
        # –ü–æ–ª—É—á–∞–µ–º –¥–∞–Ω–Ω—ã–µ –∏–∑ FSM
        data = await state.get_data()
        goal = data.get("goal")
        user_id = str(message.from_user.id)

        # –ü—Ä–æ–≤–µ—Ä—è–µ–º –≤–≤–æ–¥ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
        if message.text.isdigit():
            # –£—Å—Ç–∞–Ω–æ–≤–∫–∞ –≤—Ä–µ–º–µ–Ω–∏ –≤ –º–∏–Ω—É—Ç–∞—Ö
            n = int(message.text)
            if n <= 0 or n > 100:
                await message.answer("–í–≤–µ–¥–∏—Ç–µ —á–∏—Å–ª–æ –º–∏–Ω—É—Ç –æ—Ç 1 –¥–æ 100.")
                return
            reminder_time = datetime.now() + timedelta(minutes=n)
        else:
            # –£—Å—Ç–∞–Ω–æ–≤–∫–∞ –∫–æ–Ω–∫—Ä–µ—Ç–Ω–æ–π –¥–∞—Ç—ã –∏ –≤—Ä–µ–º–µ–Ω–∏
            reminder_time = datetime.strptime(message.text, "%Y-%m-%d %H:%M")
            if reminder_time <= datetime.now():
                await message.answer("–í—ã –Ω–µ –º–æ–∂–µ—Ç–µ —É—Å—Ç–∞–Ω–æ–≤–∏—Ç—å –Ω–∞–ø–æ–º–∏–Ω–∞–Ω–∏–µ –Ω–∞ –ø—Ä–æ—à–µ–¥—à–µ–µ –≤—Ä–µ–º—è.")
                return

        # –ü–ª–∞–Ω–∏—Ä—É–µ–º –Ω–∞–ø–æ–º–∏–Ω–∞–Ω–∏–µ
        await schedule_reminder(user_id, goal, reminder_time)

        # –°–æ—Ö—Ä–∞–Ω—è–µ–º –Ω–∞–ø–æ–º–∏–Ω–∞–Ω–∏–µ
        add_reminder_to_file(user_id, goal, reminder_time)
        await message.answer(
            f"–ù–∞–ø–æ–º–∏–Ω–∞–Ω–∏–µ –¥–ª—è —Ü–µ–ª–∏ \"{goal['name']}\" —É—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–æ –Ω–∞ {reminder_time.strftime('%Y-%m-%d %H:%M')} ‚è∞",
            reply_markup=get_main_keyboard()
        )
    except ValueError:
        await message.answer("–ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –≤–≤–µ–¥–∏—Ç–µ –∫–æ—Ä—Ä–µ–∫—Ç–Ω–æ–µ –≤—Ä–µ–º—è –≤ —Ñ–æ—Ä–º–∞—Ç–µ '10' –∏–ª–∏ '2025-01-21 12:30'.")
        return
    finally:
        await state.clear()


# –ê—Å–∏–Ω—Ö—Ä–æ–Ω–Ω—ã–π –º–µ—Ç–æ–¥ –¥–ª—è –ø–ª–∞–Ω–∏—Ä–æ–≤–∞–Ω–∏—è –Ω–∞–ø–æ–º–∏–Ω–∞–Ω–∏—è
async def schedule_reminder(user_id, goal, reminder_time):
    delay = (reminder_time - datetime.now()).total_seconds()
    if delay > 0:
        await asyncio.sleep(delay)  # –û–∂–∏–¥–∞–µ–º –¥–æ –≤—Ä–µ–º–µ–Ω–∏ –Ω–∞–ø–æ–º–∏–Ω–∞–Ω–∏—è
        await send_reminder(user_id, goal)


# –§—É–Ω–∫—Ü–∏—è –¥–ª—è –æ—Ç–ø—Ä–∞–≤–∫–∏ –Ω–∞–ø–æ–º–∏–Ω–∞–Ω–∏—è
async def send_reminder(user_id, goal):
    await bot.send_message(user_id, f"–ù–∞–ø–æ–º–∏–Ω–∞–Ω–∏–µ: –¥–æ—Å—Ç–∏–≥–∞–π—Ç–µ –≤–∞—à—É —Ü–µ–ª—å '{goal['name']}'!")


# –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –∫–Ω–æ–ø–∫–∏ "–ù–∞–ø–æ–º–∏–Ω–∞–Ω–∏—è –æ —Ü–µ–ª—è—Ö"
@dp.callback_query(lambda c: c.data == 'notion')
async def show_goals_for_reminders(call: CallbackQuery):
    user_id = call.from_user.id
    goals = load_goals_from_file(user_id)
    if not goals:
        await call.message.answer("–£ –≤–∞—Å –Ω–µ—Ç —Ü–µ–ª–µ–π, –¥–æ–±–∞–≤—å—Ç–µ —Ü–µ–ª—å —á–µ—Ä–µ–∑ –∫–æ–º–∞–Ω–¥—É '–ó–∞–ø–∏—Å–∞—Ç—å —Ü–µ–ª—å'.")
        return
    # –°–æ–∑–¥–∞–µ–º –º–µ–Ω—é —Å —Ü–µ–ª—è–º–∏
    builder = ReplyKeyboardBuilder()
    for goal in goals:
        goal_name = goal.get("name") or "–ë–µ–∑ –Ω–∞–∑–≤–∞–Ω–∏—è"
        builder.add(KeyboardButton(text=goal_name))
    builder.add(KeyboardButton(text="üîô –í–µ—Ä–Ω—É—Ç—å—Å—è –≤ –º–µ–Ω—é"))
    keyboard = builder.as_markup(resize_keyboard=True)
    await call.message.answer("–í—ã–±–µ—Ä–∏—Ç–µ —Ü–µ–ª—å –¥–ª—è —É—Å—Ç–∞–Ω–æ–≤–∫–∏ –Ω–∞–ø–æ–º–∏–Ω–∞–Ω–∏—è:", reply_markup=keyboard)

# –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –¥–ª—è –≤—ã–±–æ—Ä–∞ —Ü–µ–ª–∏ (—Ç–æ–ª—å–∫–æ –∫–æ–≥–¥–∞ –≤ —Å–æ—Å—Ç–æ—è–Ω–∏–∏ –æ–∂–∏–¥–∞–Ω–∏—è —Ü–µ–ª–∏)
@dp.message(StateFilter(GoalStates.waiting_for_goal))
async def process_reminder_goal(message: types.Message, state: FSMContext):
    if message.text == "–û—Ç–º–µ–Ω–∞":
        await state.clear()
        await message.answer("–£—Å—Ç–∞–Ω–æ–≤–∫–∞ –Ω–∞–ø–æ–º–∏–Ω–∞–Ω–∏—è –æ—Ç–º–µ–Ω–µ–Ω–∞.", reply_markup=get_main_keyboard())
        return
    # –ó–∞–≥—Ä—É–∂–∞–µ–º —Ü–µ–ª–∏ –∏–∑ —Ñ–∞–π–ª–∞ –∏ –Ω–∞—Ö–æ–¥–∏–º –≤—ã–±—Ä–∞–Ω–Ω—É—é —Ü–µ–ª—å
    user_id = str(message.from_user.id)
    goals = load_goals_from_file(user_id)
    selected_goal = next((goal for goal in goals if goal.get("name") == message.text), None)
    if not selected_goal:
        await message.answer("–¶–µ–ª—å –Ω–µ –Ω–∞–π–¥–µ–Ω–∞. –ü–æ–ø—Ä–æ–±—É–π—Ç–µ —Å–Ω–æ–≤–∞.")
        return
    # –°–æ—Ö—Ä–∞–Ω—è–µ–º –≤—ã–±—Ä–∞–Ω–Ω—É—é —Ü–µ–ª—å
    await state.update_data(goal=selected_goal)
    await message.answer(
        "–í—ã —Ö–æ—Ç–∏—Ç–µ —É—Å—Ç–∞–Ω–æ–≤–∏—Ç—å –Ω–∞–ø–æ–º–∏–Ω–∞–Ω–∏–µ —á–µ—Ä–µ–∑ –æ–ø—Ä–µ–¥–µ–ª—ë–Ω–Ω–æ–µ –≤—Ä–µ–º—è (–≤ –º–∏–Ω—É—Ç–∞—Ö) –∏–ª–∏ –Ω–∞ –∫–æ–Ω–∫—Ä–µ—Ç–Ω—É—é –¥–∞—Ç—É –∏ –≤—Ä–µ–º—è?\n"
        "–ù–∞–ø–∏—à–∏—Ç–µ, –Ω–∞–ø—Ä–∏–º–µ—Ä:\n"
        "1. '10' (—á–µ—Ä–µ–∑ 10 –º–∏–Ω—É—Ç)\n"
        "2. '2025-01-21 12:30' (–Ω–∞ –∫–æ–Ω–∫—Ä–µ—Ç–Ω—É—é –¥–∞—Ç—É –∏ –≤—Ä–µ–º—è)"
    )
    await state.set_state(GoalStates.waiting_for_time)


# –§—É–Ω–∫—Ü–∏—è –¥–ª—è –¥–æ–±–∞–≤–ª–µ–Ω–∏—è –Ω–∞–ø–æ–º–∏–Ω–∞–Ω–∏–π –≤ —Ñ–∞–π–ª
def add_reminder_to_file(user_id, selected_goal, reminder_time):
    file_name = f'reminders_{user_id}.json'
    reminder_data = {
        "goal_name": selected_goal["name"],  # –ò—Å–ø–æ–ª—å–∑—É–µ–º –ø–æ–ª–µ "name"
        "reminder_time": reminder_time.strftime("%Y-%m-%d %H:%M"),
    }
    if os.path.exists(file_name):
        with open(file_name, 'r+', encoding='utf-8') as file:
            reminders = json.load(file)
            reminders.append(reminder_data)
            file.seek(0)
            json.dump(reminders, file, ensure_ascii=False, indent=4)
    else:
        with open(file_name, 'w', encoding='utf-8') as file:
            json.dump([reminder_data], file, ensure_ascii=False, indent=4)
# –ö–û–ù–ï–¶ –¶–ï–õ–ò


# –ö–ù–û–ü–ö–ê 4 "ü§≥ –ü–æ–≥–æ–≤–æ—Ä–∏ —Å–æ –º–Ω–æ–π"
# –°–æ–∑–¥–∞–Ω–∏–µ –≥–ª–æ–±–∞–ª—å–Ω–æ–≥–æ —Å–ª–æ–≤–∞—Ä—è –∏–ª–∏ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–µ FSM
class ConversationStates(StatesGroup):
    chatting = State()

# –ö–ù–û–ü–ö–ê 4 "ü§≥ –ü–æ–≥–æ–≤–æ—Ä–∏ —Å–æ –º–Ω–æ–π"
from aiogram.types import ReplyKeyboardRemove  # –î–ª—è —Å–∫—Ä—ã—Ç–∏—è –∫–ª–∞–≤–∏–∞—Ç—É—Ä—ã

@dp.message(lambda message: "ü§≥ –ü–æ–≥–æ–≤–æ—Ä–∏ —Å–æ –º–Ω–æ–π" in message.text)  # –§–∏–ª—å—Ç—Ä –ø–æ —Ç–µ–∫—Å—Ç—É –∫–Ω–æ–ø–∫–∏
async def start_conversation(message: types.Message, state: FSMContext):
    """–ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è –¥–∏–∞–ª–æ–≥–∞ —Å GigaChat."""
    # –°–æ–∑–¥–∞–Ω–∏–µ –∫–ª–∞–≤–∏–∞—Ç—É—Ä—ã —Å –∫–Ω–æ–ø–∫–æ–π "–ó–∞–∫–æ–Ω—á–∏—Ç—å —Ä–∞–∑–≥–æ–≤–æ—Ä"
    keyboard = ReplyKeyboardMarkup(
        keyboard=[[KeyboardButton(text="–ó–∞–∫–æ–Ω—á–∏—Ç—å —Ä–∞–∑–≥–æ–≤–æ—Ä")]],  # –ö–Ω–æ–ø–∫–∞ –¥–ª—è –∑–∞–≤–µ—Ä—à–µ–Ω–∏—è –¥–∏–∞–ª–æ–≥–∞
        resize_keyboard=True
    )
    # –£—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º —Å–æ—Å—Ç–æ—è–Ω–∏–µ "chatting"
    await state.set_state(ConversationStates.chatting)
    await message.answer("–ü—Ä–∏–≤–µ—Ç! –Ø —Ç–≤–æ–π –ø–æ–º–æ—â–Ω–∏–∫ –∏ –≤—Å–µ–≥–¥–∞ —Ä–∞–¥ –ø–æ–±–æ–ª—Ç–∞—Ç—å) –û —á—ë–º —Ç—ã —Ö–æ—á–µ—à—å –ø–æ–≥–æ–≤–æ—Ä–∏—Ç—å?", reply_markup=keyboard)


@dp.message(lambda message: "–ó–∞–∫–æ–Ω—á–∏—Ç—å —Ä–∞–∑–≥–æ–≤–æ—Ä" in message.text)
async def end_conversation(message: types.Message, state: FSMContext):
    """–û–±—Ä–∞–±–æ—Ç–∫–∞ –∑–∞–≤–µ—Ä—à–µ–Ω–∏—è —Ä–∞–∑–≥–æ–≤–æ—Ä–∞."""
    # –ó–∞–≤–µ—Ä—à–∞–µ–º —Å–æ—Å—Ç–æ—è–Ω–∏–µ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
    await state.clear()  # –û—á–∏—Å—Ç–∫–∞ —Å–æ—Å—Ç–æ—è–Ω–∏—è FSM
    # –í–æ–∑–≤—Ä–∞—â–∞–µ–º –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è –≤ –≥–ª–∞–≤–Ω–æ–µ –º–µ–Ω—é
    await message.answer(
        "–ü—Ä–æ—â–∞–π! –ù–∞–¥–µ—é—Å—å, —Ç–µ–±–µ —Å—Ç–∞–ª–æ –ª–µ–≥—á–µ. –ï—Å–ª–∏ –∑–∞—Ö–æ—á–µ—à—å –ø–æ–≥–æ–≤–æ—Ä–∏—Ç—å —Å–Ω–æ–≤–∞, —è –≤—Å–µ–≥–¥–∞ –∑–¥–µ—Å—å. –í–æ–∑–≤—Ä–∞—â–∞–π—Å—è –≤ –≥–ª–∞–≤–Ω–æ–µ –º–µ–Ω—é.",
        reply_markup=ReplyKeyboardRemove()  # –°–∫—Ä—ã—Ç–∏–µ –∫–ª–∞–≤–∏–∞—Ç—É—Ä—ã
    )
    await cmd_menu1(message)  # –í–æ–∑–≤—Ä–∞—Ç –≤ –≥–ª–∞–≤–Ω–æ–µ –º–µ–Ω—é


# –û–±—Ä–∞–±–æ—Ç–∫–∞ —Å–æ–æ–±—â–µ–Ω–∏–π –≤ —Ä–∞–º–∫–∞—Ö —Ä–∞–∑–≥–æ–≤–æ—Ä–∞
@dp.message(StateFilter(ConversationStates.chatting))
async def handle_chat_message(message: types.Message, state: FSMContext):
    """–û–±—Ä–∞–±–æ—Ç–∫–∞ —Å–æ–æ–±—â–µ–Ω–∏–π –≤ —Ä–µ–∂–∏–º–µ –∞–∫—Ç–∏–≤–Ω–æ–≥–æ —Ä–∞–∑–≥–æ–≤–æ—Ä–∞."""
    # –û—Ç–ø—Ä–∞–≤–ª—è–µ–º —Å–æ–æ–±—â–µ–Ω–∏–µ –≤ GigaChat –∏ –ø–æ–ª—É—á–∞–µ–º –æ—Ç–≤–µ—Ç
    response_from_gigachat = send_to_gigachat(message.text)
    # –û—Ç–ø—Ä–∞–≤–ª—è–µ–º –æ—Ç–≤–µ—Ç –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—é
    await message.answer(response_from_gigachat)


# –û–±—Ä–∞–±–æ—Ç–∫–∞ —Å–æ–æ–±—â–µ–Ω–∏–π –≤–Ω–µ –∞–∫—Ç–∏–≤–Ω–æ–≥–æ —Ä–∞–∑–≥–æ–≤–æ—Ä–∞
@dp.message(StateFilter(None))  # –°—Ä–∞–±–∞—Ç—ã–≤–∞–µ—Ç —Ç–æ–ª—å–∫–æ –µ—Å–ª–∏ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å –Ω–µ –Ω–∞—Ö–æ–¥–∏—Ç—Å—è –Ω–∏ –≤ –æ–¥–Ω–æ–º —Å–æ—Å—Ç–æ—è–Ω–∏–∏
async def handle_message_outside_conversation(message: types.Message):
    """–û—Ç–≤–µ—Ç –Ω–∞ —Å–æ–æ–±—â–µ–Ω–∏—è –≤–Ω–µ –∞–∫—Ç–∏–≤–Ω–æ–≥–æ –¥–∏–∞–ª–æ–≥–∞."""
    # –°–æ–æ–±—â–∞–µ–º –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—é, —á—Ç–æ —á–∞—Ç –Ω–µ–∞–∫—Ç–∏–≤–µ–Ω
    await message.answer(f"–£–≤–∞–∂–∞–µ–º—ã–π {message.from_user.first_name}, –Ω–µ–ª—å–∑—è –ø–∏—Å–∞—Ç—å –ø—Ä–æ–∏–∑–≤–æ–ª—å–Ω—ã–π —Ç–µ–∫—Å—Ç! –î–ª—è –Ω–∞—á–∞–ª–∞ —Ä–∞–∑–≥–æ–≤–æ—Ä–∞ –Ω–∞–∂–º–∏—Ç–µ –∫–Ω–æ–ø–∫—É 'ü§≥ –ü–æ–≥–æ–≤–æ—Ä–∏ —Å–æ –º–Ω–æ–π'.")
# –ö–û–ù–ï–¶ –ö–ù–û–ü–ö–ò 4 "ü§≥ –ü–æ–≥–æ–≤–æ—Ä–∏ —Å–æ –º–Ω–æ–π"


# –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –ø—Ä–æ–∏–∑–≤–æ–ª—å–Ω–æ–≥–æ —Ç–µ–∫—Å—Ç–∞
@dp.message()
async def handle_message(message: Message, state: FSMContext):
    """–û—Ç–≤–µ—Ç –Ω–∞ –ø—Ä–æ–∏–∑–≤–æ–ª—å–Ω—ã–π —Ç–µ–∫—Å—Ç –æ—Ç –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è."""

    # –ü–æ–ª—É—á–∞–µ–º –¥–∞–Ω–Ω—ã–µ –∏–∑ —Å–æ—Å—Ç–æ—è–Ω–∏—è
    user_data = await state.get_data()

    # –ü—Ä–æ–≤–µ—Ä—è–µ–º, –∑–∞–≤–µ—Ä—à—ë–Ω –ª–∏ —Ä–∞–∑–≥–æ–≤–æ—Ä
    if user_data.get("conversation_ended"):
        # –ï—Å–ª–∏ —Ä–∞–∑–≥–æ–≤–æ—Ä –∑–∞–≤–µ—Ä—à—ë–Ω, –∏–≥–Ω–æ—Ä–∏—Ä—É–µ–º –ø—Ä–æ–∏–∑–≤–æ–ª—å–Ω—ã–µ —Å–æ–æ–±—â–µ–Ω–∏—è
        await message.answer(f"–£–≤–∞–∂–∞–µ–º—ã–π {message.from_user.first_name}, –Ω–µ–ª—å–∑—è –ø–∏—Å–∞—Ç—å –ø—Ä–æ–∏–∑–≤–æ–ª—å–Ω—ã–π —Ç–µ–∫—Å—Ç! –ï—Å–ª–∏ —Ö–æ—Ç–∏—Ç–µ –ø–æ–±–æ–ª—Ç–∞—Ç—å —Å –ø–æ–º–æ—â–Ω–∏–∫–æ–º, –Ω–∞–ø–∏—à–∏—Ç–µ '–ó–∞–∫–æ–Ω—á–∏—Ç—å —Ä–∞–∑–≥–æ–≤–æ—Ä', —á—Ç–æ–±—ã –Ω–∞—á–∞—Ç—å –∑–∞–Ω–æ–≤–æ.")
    else:
        # –ü—Ä–æ–≤–µ—Ä—è–µ–º, –µ—Å—Ç—å –ª–∏ –∞–∫—Ç–∏–≤–Ω–æ–µ —Å–æ—Å—Ç–æ—è–Ω–∏–µ —É –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
        current_state = await state.get_state()

        # –ï—Å–ª–∏ —Å–æ—Å—Ç–æ—è–Ω–∏–µ –ø—É—Å—Ç–æ–µ, —ç—Ç–æ –∑–Ω–∞—á–∏—Ç, —á—Ç–æ –±–æ—Ç –Ω–µ –æ–∂–∏–¥–∞–µ—Ç –≤–≤–æ–¥–∞ –¥–∞–Ω–Ω—ã—Ö
        if current_state is None:
            await message.answer(f"–£–≤–∞–∂–∞–µ–º—ã–π {message.from_user.first_name}, –Ω–µ–ª—å–∑—è –ø–∏—Å–∞—Ç—å –ø—Ä–æ–∏–∑–≤–æ–ª—å–Ω—ã–π —Ç–µ–∫—Å—Ç!")
        else:
            # –ï—Å–ª–∏ —Å–æ—Å—Ç–æ—è–Ω–∏–µ –∞–∫—Ç–∏–≤–Ω–æ, –∑–Ω–∞—á–∏—Ç, –±–æ—Ç –æ–∂–∏–¥–∞–µ—Ç –∫–æ–Ω–∫—Ä–µ—Ç–Ω—ã–π –æ—Ç–≤–µ—Ç
            return

# –ó–∞–ø—É—Å–∫ –±–æ—Ç–∞ –∏ –Ω–∞—Å—Ç—Ä–æ–π–∫–∞ –∫–æ–º–∞–Ω–¥
async def start_bot():
    """–ù–∞—Å—Ç—Ä–æ–π–∫–∞ –∫–æ–º–∞–Ω–¥ –¥–ª—è –±–æ—Ç–∞."""
    commands = [
        BotCommand(command='menu1', description='–î–æ–ø. —Ñ—É–Ω–∫—Ü–∏–∏'),
        BotCommand(command='menu2', description='–¢–≤–æ–π –¥–Ω–µ–≤–Ω–∏–∫ –∏ —Ü–µ–ª–∏')
    ]
    await bot.set_my_commands(commands, BotCommandScopeDefault())

# –û—Å–Ω–æ–≤–Ω–∞—è –∞—Å–∏–Ω—Ö—Ä–æ–Ω–Ω–∞—è —Ñ—É–Ω–∫—Ü–∏—è
async def main():
    """–ó–∞–ø—É—Å–∫ –±–æ—Ç–∞."""
    await create_db()  # –°–æ–∑–¥–∞–µ–º —Ç–∞–±–ª–∏—Ü—É –ø—Ä–∏ –∑–∞–ø—É—Å–∫–µ
    dp.startup.register(start_bot)
    try:
        print("–ë–æ—Ç –∑–∞–ø—É—â–µ–Ω...")
        await bot.delete_webhook(drop_pending_updates=True)
        await dp.start_polling(bot, allowed_updates=dp.resolve_used_update_types())  # –ó–∞–ø—É—Å–∫ –±–æ—Ç–∞ –≤ —Ä–µ–∂–∏–º–µ –æ–ø—Ä–æ—Å–∞
    except asyncio.CancelledError:
        print("–ó–∞–¥–∞—á–∞ –±—ã–ª–∞ –æ—Ç–º–µ–Ω–µ–Ω–∞. –ó–∞–≤–µ—Ä—à–∞–µ–º —Ä–∞–±–æ—Ç—É –±–æ—Ç–∞...")
    finally:
        await bot.session.close()
        print("–ë–æ—Ç –æ—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω")

# –ó–∞–ø—É—Å–∫ –æ—Å–Ω–æ–≤–Ω–æ–π –∞—Å–∏–Ω—Ö—Ä–æ–Ω–Ω–æ–π —Ñ—É–Ω–∫—Ü–∏–∏
asyncio.run(main())