def check_name(name: str):
    return len(name.split()) == 3

def check_age(age: str):
    return age.isdigit()

# –§—É–Ω–∫—Ü–∏—è –¥–ª—è –ø–æ–ª—É—á–µ–Ω–∏—è –≤—Å–µ—Ö user_id –∏–∑ –±–∞–∑—ã –¥–∞–Ω–Ω—ã—Ö
async def get_all_users():
    async with aiosqlite.connect("users.db") as db:
        async with db.execute("SELECT user_id FROM users") as cursor:
            users = await cursor.fetchall()
            return [user[0] for user in users]  # –í–æ–∑–≤—Ä–∞—â–∞–µ–º —Å–ø–∏—Å–æ–∫ –≤—Å–µ—Ö user_id


@dp.message(F.text, Form.name)
async def inputfio(message: Message, state: FSMContext):
    if not check_name(message.text):
        await message.answer(f'–§–ò–û –≤–≤–µ–¥–µ–Ω–æ –Ω–µ–∫–æ—Ä—Ä–µ–∫—Ç–Ω–æ. –ü–æ–≤—Ç–æ—Ä–∏—Ç–µ –≤–≤–æ–¥')
        return
    await message.answer(f'–§–ò–û –ø—Ä–∏–Ω—è—Ç–æ! –¢–µ–ø–µ—Ä—å –≤–≤–µ–¥–∏—Ç–µ –≤–∞—à –≤–æ–∑—Ä–∞—Å—Ç —Ü–∏—Ñ—Ä–∞–º–∏:')
    await state.update_data(name=message.text)
    await state.set_state(Form.age)

@dp.message(F.text, Form.age)
async def input_age(message: Message, state: FSMContext):
    if not check_age(message.text):
        await message.answer(f'–í–æ–∑—Ä–∞—Å—Ç –≤–≤–µ–¥–µ–Ω –Ω–µ–∫–æ—Ä—Ä–µ–∫—Ç–Ω–æ. –ü–æ–≤—Ç–æ—Ä–∏—Ç–µ –≤–≤–æ–¥ (—Ç–æ–ª—å–∫–æ —á–∏—Å–ª–æ)')
        return
    data = await state.get_data()
    await add_user_to_db(message.from_user.id, data['name'], message.from_user.first_name, message.text)
    await message.answer(f'–î–∞–Ω–Ω—ã–µ —Å–æ—Ö—Ä–∞–Ω–µ–Ω—ã! –í–∞—à–∏ –¥–∞–Ω–Ω—ã–µ: \n–§–ò–û - {data["name"]} \n–≤–æ–∑—Ä–∞—Å—Ç - {message.text} \n–≤–∞—à id = {message.from_user.id}')
    await message.answer(f'‚úÖ –û—Ç–ª–∏—á–Ω–æ, —Ä–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏—è –∑–∞–≤–µ—Ä—à–µ–Ω–∞! –¢–µ–ø–µ—Ä—å –≤—ã –º–æ–∂–µ—Ç–µ –Ω–∞—á–∞—Ç—å –ø—É—Ç–µ—à–µ—Å—Ç–≤–∏–µ –∫ –æ—Å–æ–∑–Ω–∞–Ω–Ω–æ—Å—Ç–∏. \n‚ú® –ù–∞–∂–∏–º–∞–π—Ç–µ –Ω–∞ –∫–Ω–æ–ø–∫—É "–ú–µ–Ω—é", —á—Ç–æ–±—ã —É–≤–∏–¥–µ—Ç—å –≤—Å–µ –¥–æ—Å—Ç—É–ø–Ω—ã–µ –≤–æ–∑–º–æ–∂–Ω–æ—Å—Ç–∏: –º–µ–¥–∏—Ç–∞—Ü–∏–∏, –¥—ã—Ö–∞—Ç–µ–ª—å–Ω—ã–µ —É–ø—Ä–∞–∂–Ω–µ–Ω–∏—è, –¥–Ω–µ–≤–Ω–∏–∫ –∏ –º–Ω–æ–≥–æ–µ –¥—Ä—É–≥–æ–µ.')
    await state.clear()  # –û–±—è–∑–∞—Ç–µ–ª—å–Ω–æ –æ—á–∏—â–∞–µ–º —Å–æ—Å—Ç–æ—è–Ω–∏–µ

'''
# –ö–æ–º–∞–Ω–¥–∞ /start –¥–ª—è –ø—Ä–∏–≤–µ—Ç—Å—Ç–≤–∏—è –∏ –≤–≤–æ–¥–∞ –¥–∞–Ω–Ω—ã—Ö
@dp.message(CommandStart(), State(None))
async def cmd_start(message: Message, state: FSMContext):
    """–û–±—Ä–∞–±–æ—Ç—á–∏–∫ –∫–æ–º–∞–Ω–¥—ã /start. –ó–∞–ø—Ä–∞—à–∏–≤–∞–µ—Ç —É –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è –§–ò–û."""
    await message.answer(f'‚ú® –î–æ–±—Ä–æ –ø–æ–∂–∞–ª–æ–≤–∞—Ç—å! \n–≠—Ç–æ—Ç –±–æ—Ç –ø–æ–º–æ–∂–µ—Ç –≤–∞–º —Ä–∞–∑–≤–∏–≤–∞—Ç—å –æ—Å–æ–∑–Ω–∞–Ω–Ω–æ—Å—Ç—å –∏ —É–ª—É—á—à–∞—Ç—å –∫–∞—á–µ—Å—Ç–≤–æ –∂–∏–∑–Ω–∏. –í—ã —Å–º–æ–∂–µ—Ç–µ –º–µ–¥–∏—Ç–∏—Ä–æ–≤–∞—Ç—å, –∑–∞–ø–∏—Å—ã–≤–∞—Ç—å –º—ã—Å–ª–∏, –ø–æ–ª—É—á–∞—Ç—å —Å–æ–≤–µ—Ç—ã –∏ –Ω–∞–ø–æ–º–∏–Ω–∞–Ω–∏—è –¥–ª—è –æ—Å–æ–∑–Ω–∞–Ω–Ω—ã—Ö –ø–∞—É–∑ üåø \n–î–∞–≤–∞–π—Ç–µ –Ω–∞—á–Ω–µ–º! –î–ª—è —ç—Ç–æ–≥–æ –Ω—É–∂–Ω–æ —Å–Ω–∞—á–∞–ª–∞ –ø—Ä–æ–π—Ç–∏ —Ä–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏—é.')
    await message.answer(f'–ò—Ç–∞–∫, {message.from_user.first_name}! –í–≤–µ–¥–∏—Ç–µ –≤–∞—à–µ –§–ò–û:')
    await state.set_state(Form.name)  # –ü–µ—Ä–µ—Ö–æ–¥ –∫ —Å–æ—Å—Ç–æ—è–Ω–∏—é –≤–≤–æ–¥–∞ –§–ò–û
'''

"""–û–±—Ä–∞–±–æ—Ç—á–∏–∫ –∫–æ–º–∞–Ω–¥—ã /start. –ó–∞–ø—Ä–∞—à–∏–≤–∞–µ—Ç —É –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è –§–ò–û."""
@dp.message(CommandStart(), State(None))
async def cmd_start(message: Message, state: FSMContext):
    user_id = message.from_user.id
    user = await get_user(user_id)
    if user:  # –ï—Å–ª–∏ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å –Ω–∞–π–¥–µ–Ω –≤ –±–∞–∑–µ, —Ç–æ –æ–Ω —É–∂–µ –∑–∞—Ä–µ–≥–∏—Å—Ç—Ä–∏—Ä–æ–≤–∞–Ω
        await message.answer(f"–° –≤–æ–∑–≤—Ä–∞—â–µ–Ω–∏–µ–º, {message.from_user.first_name}! –í—ã —É–∂–µ –∑–∞—Ä–µ–≥–∏—Å—Ç—Ä–∏—Ä–æ–≤–∞–Ω—ã.")
        await message.answer("–í—ã–±–µ—Ä–∏—Ç–µ –¥–µ–π—Å—Ç–≤–∏—è, –Ω–∞–ø—Ä–∏–º–µ—Ä, /start –¥–ª—è –Ω–∞—á–∞–ª–∞ —Ä–∞–±–æ—Ç—ã —Å –±–æ—Ç–æ–º.")
    else:  # –ï—Å–ª–∏ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å –Ω–µ –Ω–∞–π–¥–µ–Ω, —Ç–æ –Ω–∞—á–∏–Ω–∞–µ–º –ø—Ä–æ—Ü–µ—Å—Å —Ä–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏–∏
        await message.answer(f'‚ú® –î–æ–±—Ä–æ –ø–æ–∂–∞–ª–æ–≤–∞—Ç—å! \n–≠—Ç–æ—Ç –±–æ—Ç –ø–æ–º–æ–∂–µ—Ç –≤–∞–º —Ä–∞–∑–≤–∏–≤–∞—Ç—å –æ—Å–æ–∑–Ω–∞–Ω–Ω–æ—Å—Ç—å –∏ —É–ª—É—á—à–∞—Ç—å –∫–∞—á–µ—Å—Ç–≤–æ –∂–∏–∑–Ω–∏. –í—ã —Å–º–æ–∂–µ—Ç–µ –º–µ–¥–∏—Ç–∏—Ä–æ–≤–∞—Ç—å, –∑–∞–ø–∏—Å—ã–≤–∞—Ç—å –º—ã—Å–ª–∏, –ø–æ–ª—É—á–∞—Ç—å —Å–æ–≤–µ—Ç—ã –∏ –Ω–∞–ø–æ–º–∏–Ω–∞–Ω–∏—è –¥–ª—è –æ—Å–æ–∑–Ω–∞–Ω–Ω—ã—Ö –ø–∞—É–∑ üåø \n–î–∞–≤–∞–π—Ç–µ –Ω–∞—á–Ω–µ–º! –î–ª—è —ç—Ç–æ–≥–æ –Ω—É–∂–Ω–æ —Å–Ω–∞—á–∞–ª–∞ –ø—Ä–æ–π—Ç–∏ —Ä–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏—é.')
        await message.answer(f'–ò—Ç–∞–∫, {message.from_user.first_name}! –í–≤–µ–¥–∏—Ç–µ –≤–∞—à–µ –§–ò–û:')
        await state.set_state(Form.name)  # –ü–µ—Ä–µ—Ö–æ–¥ –∫ —Å–æ—Å—Ç–æ—è–Ω–∏—é –≤–≤–æ–¥–∞ –§–ò–û

# –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –∫–æ–º–∞–Ω–¥—ã –¥–ª—è –æ—Ç–ø—Ä–∞–≤–∫–∏ –æ–±—ã—á–Ω–æ–π –∫–ª–∞–≤–∏–∞—Ç—É—Ä—ã
@dp.message(Command("menu1"), State(None))
async def cmd_menu1(message: Message):
    """–û–±—Ä–∞–±–æ—Ç—á–∏–∫ –∫–æ–º–∞–Ω–¥—ã –¥–ª—è –æ—Ç–ø—Ä–∞–≤–∫–∏ –æ–±—ã—á–Ω–æ–π –∫–ª–∞–≤–∏–∞—Ç—É—Ä—ã."""
    builder = ReplyKeyboardBuilder()
    builder.add(KeyboardButton(text="üßò‚Äç –ú–µ–¥–∏—Ç–∞—Ü–∏–∏"))
    builder.add(KeyboardButton(text="üåü –ú–æ—Ç–∏–≤–∏—Ä—É—é—â–∏–µ —Ü–∏—Ç–∞—Ç—ã"))
    builder.add(KeyboardButton(text="üå¨Ô∏è –î—ã—Ö–∞—Ç–µ–ª—å–Ω—ã–µ —É–ø—Ä–∞–∂–Ω–µ–Ω–∏—è"))
    builder.add(KeyboardButton(text="ü§≥ –ü–æ–≥–æ–≤—Ä–∏ —Å–æ –º–Ω–æ–π"))
    builder.add(KeyboardButton(text="‚ÑπÔ∏è –û –±–æ—Ç–µ"))
    keyboard = builder.as_markup(resize_keyboard=True, one_time_keyboard=True)

    await message.answer("–í—ã–±–µ—Ä–∏—Ç–µ –¥–µ–π—Å—Ç–≤–∏–µ:", reply_markup=keyboard)

# –û—Ç–≤–µ—Ç—ã –Ω–∞ –∫–æ–º–∞–Ω–¥—ã —Å –æ–±—ã—á–Ω–æ–π –∫–ª–∞–≤–∏–∞—Ç—É—Ä–æ–π

# –ö–ù–û–ü–ö–ê 1 "üßò‚Äç –ú–µ–¥–∏—Ç–∞—Ü–∏–∏"
@dp.message(F.text == "üßò‚Äç –ú–µ–¥–∏—Ç–∞—Ü–∏–∏")
async def meditation_menu(message: Message):
    """–û—Ç–ø—Ä–∞–≤–∫–∞ –∫–ª–∞–≤–∏–∞—Ç—É—Ä—ã —Å –≤—ã–±–æ—Ä–æ–º –º–µ–¥–∏—Ç–∞—Ü–∏–∏."""
    builder = ReplyKeyboardBuilder()
    builder.add(KeyboardButton(text="–ö–æ—Ä–æ—Ç–∫–∞—è (5 –º–∏–Ω)"))
    builder.add(KeyboardButton(text="–°—Ä–µ–¥–Ω—è—è (10 –º–∏–Ω)"))
    builder.add(KeyboardButton(text="–î–ª–∏–Ω–Ω–∞—è (20 –º–∏–Ω)"))
    builder.add(KeyboardButton(text="üîô –í–µ—Ä–Ω—É—Ç—å—Å—è –≤ –º–µ–Ω—é"))
    keyboard = builder.as_markup(resize_keyboard=True)

    await message.answer("üßò –í—ã–±–µ—Ä–∏—Ç–µ –º–µ–¥–∏—Ç–∞—Ü–∏—é:", reply_markup=keyboard)

# –û—Ç–≤–µ—Ç –Ω–∞ –≤—ã–±–æ—Ä –º–µ–¥–∏—Ç–∞—Ü–∏–∏
@dp.message(F.text == "–ö–æ—Ä–æ—Ç–∫–∞—è (5 –º–∏–Ω)")
async def short_meditation(message: Message):
    """–û–±—Ä–∞–±–æ—Ç–∫–∞ –≤—ã–±–æ—Ä–∞ –∫–æ—Ä–æ—Ç–∫–æ–π –º–µ–¥–∏—Ç–∞—Ü–∏–∏."""
    await message.answer("–í—ã –≤—ã–±—Ä–∞–ª–∏ –∫–æ—Ä–æ—Ç–∫—É—é –º–µ–¥–∏—Ç–∞—Ü–∏—é (5 –º–∏–Ω—É—Ç). –î–∞–≤–∞–π—Ç–µ –Ω–∞—á–Ω—ë–º –º–µ–¥–∏—Ç–∞—Ü–∏—é...\n\n–ö–æ—Ä–æ—Ç–∫–∞—è –º–µ–¥–∏—Ç–∞—Ü–∏—è (5 –º–∏–Ω—É—Ç) \n–¶–µ–ª—å: –ë—ã—Å—Ç—Ä–æ–µ —Ä–∞—Å—Å–ª–∞–±–ª–µ–Ω–∏–µ –∏ —Ñ–æ–∫—É—Å–∏—Ä–æ–≤–∫–∞ –Ω–∞ –¥—ã—Ö–∞–Ω–∏–∏ –¥–ª—è —Å–Ω—è—Ç–∏—è —Å—Ç—Ä–µ—Å—Å–∞ –∏–ª–∏ –Ω–∞–ø—Ä—è–∂–µ–Ω–∏—è. \n\n–®–∞–≥–∏:\n1. –°–µ—Å—Ç—å —É–¥–æ–±–Ω–æ. –ü—Ä–∏–º–∏—Ç–µ —É–¥–æ–±–Ω–æ–µ –ø–æ–ª–æ–∂–µ–Ω–∏–µ ‚Äî –º–æ–∂–Ω–æ —Å–∏–¥–µ—Ç—å —Å –ø—Ä—è–º–æ–π —Å–ø–∏–Ω–æ–π, —Ä–∞—Å—Å–ª–∞–±–∏–≤ –ø–ª–µ—á–∏, –∏–ª–∏ –ª–µ—á—å.\n2. –ó–∞–∫—Ä—ã—Ç—å –≥–ª–∞–∑–∞. –°–¥–µ–ª–∞–π—Ç–µ –Ω–µ—Å–∫–æ–ª—å–∫–æ –≥–ª—É–±–æ–∫–∏—Ö –≤–¥–æ—Ö–æ–≤ –∏ –≤—ã–¥–æ—Ö–æ–≤, —á—Ç–æ–±—ã —Ä–∞—Å—Å–ª–∞–±–∏—Ç—å—Å—è. \n3. –§–æ–∫—É—Å–∏—Ä–æ–≤–∫–∞ –Ω–∞ –¥—ã—Ö–∞–Ω–∏–∏. –°–æ—Å—Ä–µ–¥–æ—Ç–æ—á—å—Ç–µ—Å—å –Ω–∞ —Å–≤–æ–µ–º –¥—ã—Ö–∞–Ω–∏–∏. –í–¥—ã—Ö–∞–π—Ç–µ –≥–ª—É–±–æ–∫–æ —á–µ—Ä–µ–∑ –Ω–æ—Å, –æ—â—É—â–∞—è, –∫–∞–∫ –≤–æ–∑–¥—É—Ö –∑–∞–ø–æ–ª–Ω—è–µ—Ç –∂–∏–≤–æ—Ç, –∏ –≤—ã–¥—ã—Ö–∞–π—Ç–µ —á–µ—Ä–µ–∑ —Ä–æ—Ç, –æ—Å–≤–æ–±–æ–∂–¥–∞—è —Ç–µ–ª–æ –æ—Ç –Ω–∞–ø—Ä—è–∂–µ–Ω–∏—è.\n4. –û—Å–æ–∑–Ω–∞–Ω–∏–µ –∫–∞–∂–¥–æ–≥–æ –≤–¥–æ—Ö–∞. –í–¥–æ—Ö–Ω–∏—Ç–µ, —Å–æ—Å—á–∏—Ç–∞–π—Ç–µ –¥–æ 4, –∑–∞–¥–µ—Ä–∂–∏—Ç–µ –¥—ã—Ö–∞–Ω–∏–µ –Ω–∞ 4 —Å–µ–∫—É–Ω–¥—ã, –∑–∞—Ç–µ–º –≤—ã–¥–æ—Ö–Ω–∏—Ç–µ –Ω–∞ 4 —Å—á–µ—Ç–∞. –≠—Ç–æ –ø–æ–º–æ–∂–µ—Ç –∑–∞–º–µ–¥–ª–∏—Ç—å –¥—ã—Ö–∞–Ω–∏–µ –∏ —É—Å–ø–æ–∫–æ–∏—Ç—å —É–º. \n5. –ü–æ–≤—Ç–æ—Ä–∏—Ç–µ —Ü–∏–∫–ª. –ü–æ–≤—Ç–æ—Ä–∏—Ç–µ —Ü–∏–∫–ª –Ω–µ—Å–∫–æ–ª—å–∫–æ —Ä–∞–∑, —Å–æ—Å—Ä–µ–¥–æ—Ç–æ—á–∏–≤—à–∏—Å—å —Ç–æ–ª—å–∫–æ –Ω–∞ –¥—ã—Ö–∞–Ω–∏–∏.\n6. –ó–∞–∫–ª—é—á–µ–Ω–∏–µ. –ü–æ—Å—Ç–µ–ø–µ–Ω–Ω–æ –≤–µ—Ä–Ω–∏—Ç–µ—Å—å –≤ –æ—Å–æ–∑–Ω–∞–Ω–∏–µ –æ–∫—Ä—É–∂–∞—é—â–µ–≥–æ –º–∏—Ä–∞. –û—Ç–∫—Ä–æ–π—Ç–µ –≥–ª–∞–∑–∞, —Å–¥–µ–ª–∞–π—Ç–µ –ø–∞—Ä—É –≥–ª—É–±–æ–∫–∏—Ö –≤–¥–æ—Ö–æ–≤, –∏ –ø–æ—á—É–≤—Å—Ç–≤—É–π—Ç–µ, –∫–∞–∫ –≤–∞—à–µ —Ç–µ–ª–æ —Ä–∞—Å—Å–ª–∞–±–∏–ª–æ—Å—å.\n\n–ü–æ–¥—Å–∫–∞–∑–∫–∞: –≠—Ç–æ –æ—Ç–ª–∏—á–Ω—ã–π —Å–ø–æ—Å–æ–± –±—ã—Å—Ç—Ä–æ –≤–µ—Ä–Ω—É—Ç—å —Å–µ–±–µ —Å–ø–æ–∫–æ–π—Å—Ç–≤–∏–µ –∏ —Ñ–æ–∫—É—Å.")

@dp.message(F.text == "–°—Ä–µ–¥–Ω—è—è (10 –º–∏–Ω)")
async def medium_meditation(message: Message):
    """–û–±—Ä–∞–±–æ—Ç–∫–∞ –≤—ã–±–æ—Ä–∞ —Å—Ä–µ–¥–Ω–µ–π –º–µ–¥–∏—Ç–∞—Ü–∏–∏."""
    await message.answer("–í—ã –≤—ã–±—Ä–∞–ª–∏ —Å—Ä–µ–¥–Ω—é—é –º–µ–¥–∏—Ç–∞—Ü–∏—é (10 –º–∏–Ω—É—Ç). –î–∞–≤–∞–π—Ç–µ –Ω–∞—á–Ω—ë–º –º–µ–¥–∏—Ç–∞—Ü–∏—é...\n\n–°—Ä–µ–¥–Ω—è—è –º–µ–¥–∏—Ç–∞—Ü–∏—è (10 –º–∏–Ω—É—Ç)\n–¶–µ–ª—å: –ë–æ–ª–µ–µ –≥–ª—É–±–æ–∫–æ–µ —Ä–∞—Å—Å–ª–∞–±–ª–µ–Ω–∏–µ –∏ —Å–æ–µ–¥–∏–Ω–µ–Ω–∏–µ —Å –≤–Ω—É—Ç—Ä–µ–Ω–Ω–∏–º —Å–æ—Å—Ç–æ—è–Ω–∏–µ–º.\n\n–®–∞–≥–∏:\n1. –ù–∞–π–¥–∏—Ç–µ —Å–ø–æ–∫–æ–π–Ω–æ–µ –º–µ—Å—Ç–æ. –£–±–µ–¥–∏—Ç–µ—Å—å, —á—Ç–æ –≤–∞—Å –Ω–µ –±—É–¥—É—Ç –±–µ—Å–ø–æ–∫–æ–∏—Ç—å. –°—è–¥—å—Ç–µ –∏–ª–∏ –ª—è–≥—Ç–µ –≤ –∫–æ–º—Ñ–æ—Ä—Ç–Ω–æ–º –ø–æ–ª–æ–∂–µ–Ω–∏–∏.\n2. –ó–∞–∫—Ä–æ–π—Ç–µ –≥–ª–∞–∑–∞ –∏ —Ä–∞—Å—Å–ª–∞–±—å—Ç–µ—Å—å. –°–¥–µ–ª–∞–π—Ç–µ –Ω–µ—Å–∫–æ–ª—å–∫–æ –º–µ–¥–ª–µ–Ω–Ω—ã—Ö –≥–ª—É–±–æ–∫–∏—Ö –≤–¥–æ—Ö–æ–≤, —á—Ç–æ–±—ã —Ä–∞—Å—Å–ª–∞–±–∏—Ç—å —Ç–µ–ª–æ –∏ –æ—Å–≤–æ–±–æ–¥–∏—Ç—å—Å—è –æ—Ç –Ω–∞–ø—Ä—è–∂–µ–Ω–∏—è.\n3. –°–æ—Å—Ä–µ–¥–æ—Ç–æ—á–µ–Ω–∏–µ –Ω–∞ –¥—ã—Ö–∞–Ω–∏–∏. –ù–∞—á–Ω–∏—Ç–µ —Å–ª–µ–¥–∏—Ç—å –∑–∞ –¥—ã—Ö–∞–Ω–∏–µ–º, –∫–∞–∫ –≤ –∫–æ—Ä–æ—Ç–∫–æ–π –º–µ–¥–∏—Ç–∞—Ü–∏–∏, –Ω–æ —Ç–µ–ø–µ—Ä—å –ø–æ–∑–≤–æ–ª—å—Ç–µ —Å–µ–±–µ –±—ã—Ç—å –±–æ–ª–µ–µ –≤–Ω–∏–º–∞—Ç–µ–ª—å–Ω—ã–º –∫ –∫–∞–∂–¥–æ–º—É –≤–¥–æ—Ö—É –∏ –≤—ã–¥–æ—Ö—É. –ü–æ—Å—Ç–µ–ø–µ–Ω–Ω–æ –∑–∞–º–µ–¥–ª—è–π—Ç–µ –¥—ã—Ö–∞–Ω–∏–µ.\n4. –ú–µ–¥–∏—Ç–∞—Ü–∏—è –æ—Å–æ–∑–Ω–∞–Ω–Ω–æ—Å—Ç–∏. –ü—Ä–µ–¥—Å—Ç–∞–≤—å—Ç–µ —Å–µ–±–µ –∫—Ä–∞—Å–∏–≤–æ–µ –º–µ—Å—Ç–æ ‚Äî –ª–µ—Å, –ø–ª—è–∂, –≥–æ—Ä—ã ‚Äî –∏ –≤–∏–∑—É–∞–ª–∏–∑–∏—Ä—É–π—Ç–µ, –∫–∞–∫ –≤—ã –Ω–∞—Ö–æ–¥–∏—Ç–µ—Å—å —Ç–∞–º, –æ—â—É—â–∞—è —Å–≤–µ–∂–∏–π –≤–æ–∑–¥—É—Ö, –∑–≤—É–∫–∏ –ø—Ä–∏—Ä–æ–¥—ã. –°–æ—Å—Ä–µ–¥–æ—Ç–æ—á—å—Ç–µ—Å—å –Ω–∞ —ç—Ç–æ–º –æ—â—É—â–µ–Ω–∏–∏.\n5. –ü—Ä–∏–Ω—è—Ç–∏–µ –º—ã—Å–ª–µ–π. –ï—Å–ª–∏ –ø–æ—è–≤–ª—è—é—Ç—Å—è –º—ã—Å–ª–∏, –Ω–µ –±–æ—Ä–∏—Ç–µ—Å—å —Å –Ω–∏–º–∏. –ü—Ä–æ—Å—Ç–æ –Ω–∞–±–ª—é–¥–∞–π—Ç–µ –∑–∞ –Ω–∏–º–∏, –∫–∞–∫ –∑–∞ –æ–±–ª–∞–∫–∞–º–∏, –∫–æ—Ç–æ—Ä—ã–µ –ø—Ä–æ—Ö–æ–¥—è—Ç –º–∏–º–æ.\n6. –¢–µ–ª–æ –∏ —Ä–∞—Å—Å–ª–∞–±–ª–µ–Ω–∏–µ. –ü—Ä–æ–π–¥–∏—Ç–µ—Å—å –≤–Ω–∏–º–∞–Ω–∏–µ–º –ø–æ –≤—Å–µ–º—É —Ç–µ–ª—É, –Ω–∞—á–∏–Ω–∞—è —Å –≥–æ–ª–æ–≤—ã –∏ –¥–≤–∏–≥–∞—è—Å—å –≤–Ω–∏–∑ –∫ –Ω–æ–≥–∞–º. –ó–∞–º–µ—á–∞–π—Ç–µ, –≥–¥–µ –æ—â—É—â–∞–µ—Ç–µ –Ω–∞–ø—Ä—è–∂–µ–Ω–∏–µ, –∏ –ø–æ—Å—Ç–∞—Ä–∞–π—Ç–µ—Å—å —Ä–∞—Å—Å–ª–∞–±–∏—Ç—å —ç—Ç–∏ —É—á–∞—Å—Ç–∫–∏.\n7. –ó–∞–∫–ª—é—á–µ–Ω–∏–µ. –ó–∞–∫–æ–Ω—á–∏—Ç–µ –º–µ–¥–∏—Ç–∞—Ü–∏—é —Ç–µ–º, —á—Ç–æ –≤–æ–∑–≤—Ä–∞—â–∞–µ—Ç–µ—Å—å –≤ –æ—Å–æ–∑–Ω–∞–Ω–∏–µ –æ–∫—Ä—É–∂–∞—é—â–µ–≥–æ –º–∏—Ä–∞. –°–¥–µ–ª–∞–π—Ç–µ –Ω–µ—Å–∫–æ–ª—å–∫–æ –≥–ª—É–±–æ–∫–∏—Ö –≤–¥–æ—Ö–æ–≤ –∏ –≤—ã–¥–æ—Ö–æ–≤, –æ—Ç–∫—Ä–æ–π—Ç–µ –≥–ª–∞–∑–∞ –∏ –ø–æ—Å—Ç–∞—Ä–∞–π—Ç–µ—Å—å —Å–æ—Ö—Ä–∞–Ω–∏—Ç—å –æ—â—É—â–µ–Ω–∏–µ –≤–Ω—É—Ç—Ä–µ–Ω–Ω–µ–≥–æ —Å–ø–æ–∫–æ–π—Å—Ç–≤–∏—è.\n\n–ü–æ–¥—Å–∫–∞–∑–∫–∞: –≠—Ç–∞ –º–µ–¥–∏—Ç–∞—Ü–∏—è –ø–æ–¥—Ö–æ–¥–∏—Ç –¥–ª—è —Ç–µ—Ö, –∫—Ç–æ —Ö–æ—á–µ—Ç –±–æ–ª—å—à–µ –≤—Ä–µ–º–µ–Ω–∏ –¥–ª—è —Ä–∞—Å—Å–ª–∞–±–ª–µ–Ω–∏—è –∏ –≤–æ—Å—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–∏—è.")

@dp.message(F.text == "–î–ª–∏–Ω–Ω–∞—è (20 –º–∏–Ω)")
async def long_meditation(message: Message):
    """–û–±—Ä–∞–±–æ—Ç–∫–∞ –≤—ã–±–æ—Ä–∞ –¥–ª–∏–Ω–Ω–æ–π –º–µ–¥–∏—Ç–∞—Ü–∏–∏."""
    await message.answer('–í—ã –≤—ã–±—Ä–∞–ª–∏ –¥–ª–∏–Ω–Ω—É—é –º–µ–¥–∏—Ç–∞—Ü–∏—é (20 –º–∏–Ω—É—Ç). –î–∞–≤–∞–π—Ç–µ –Ω–∞—á–Ω—ë–º –º–µ–¥–∏—Ç–∞—Ü–∏—é...\n\n–î–ª–∏–Ω–Ω–∞—è –º–µ–¥–∏—Ç–∞—Ü–∏—è (20 –º–∏–Ω—É—Ç)\n–¶–µ–ª—å: –ì–ª—É–±–æ–∫–æ–µ —Ä–∞—Å—Å–ª–∞–±–ª–µ–Ω–∏–µ –∏ –æ—Å–æ–∑–Ω–∞–Ω–Ω–æ–µ –≤–Ω–∏–º–∞–Ω–∏–µ –∫ —Å–≤–æ–µ–º—É —Å–æ—Å—Ç–æ—è–Ω–∏—é –¥–ª—è –¥–æ—Å—Ç–∏–∂–µ–Ω–∏—è –≥–∞—Ä–º–æ–Ω–∏–∏ –∏ –±–∞–ª–∞–Ω—Å–∞.\n\n–®–∞–≥–∏:\n1. –°–æ–∑–¥–∞–π—Ç–µ –ø—Ä–æ—Å—Ç—Ä–∞–Ω—Å—Ç–≤–æ –¥–ª—è –º–µ–¥–∏—Ç–∞—Ü–∏–∏. –ù–∞–π–¥–∏—Ç–µ —Ç–∏—Ö–æ–µ –º–µ—Å—Ç–æ, –≥–¥–µ –≤–∞—Å –Ω–µ –ø–æ–±–µ—Å–ø–æ–∫–æ—è—Ç –≤ —Ç–µ—á–µ–Ω–∏–µ 20 –º–∏–Ω—É—Ç. –°—è–¥—å—Ç–µ –≤ —É–¥–æ–±–Ω–æ–µ –ø–æ–ª–æ–∂–µ–Ω–∏–µ –∏–ª–∏ –ª—è–≥—Ç–µ.\n2. –ó–∞–∫—Ä–æ–π—Ç–µ –≥–ª–∞–∑–∞ –∏ —Ä–∞—Å—Å–ª–∞–±—å—Ç–µ—Å—å. –°–¥–µ–ª–∞–π—Ç–µ –Ω–µ—Å–∫–æ–ª—å–∫–æ –≥–ª—É–±–æ–∫–∏—Ö –≤–¥–æ—Ö–æ–≤, –ø–æ–∑–≤–æ–ª—è—è —Å–≤–æ–µ–º—É —Ç–µ–ª—É –ø–æ—Å—Ç–µ–ø–µ–Ω–Ω–æ —Ä–∞—Å—Å–ª–∞–±–ª—è—Ç—å—Å—è.\n3. –û—Å–æ–∑–Ω–∞–Ω–Ω–æ–µ –¥—ã—Ö–∞–Ω–∏–µ. –°–æ—Å—Ä–µ–¥–æ—Ç–æ—á—å—Ç–µ—Å—å –Ω–∞ –¥—ã—Ö–∞–Ω–∏–∏, –∫–∞–∫ –≤ –∫–æ—Ä–æ—Ç–∫–æ–π –º–µ–¥–∏—Ç–∞—Ü–∏–∏. –ü–æ—Å—Ç–µ–ø–µ–Ω–Ω–æ —Å–¥–µ–ª–∞–π—Ç–µ –¥—ã—Ö–∞–Ω–∏–µ –±–æ–ª–µ–µ –≥–ª—É–±–æ–∫–∏–º –∏ —Ä–∞–∑–º–µ—Ä–µ–Ω–Ω—ã–º.\n4. –î—ã—Ö–∞–Ω–∏–µ —Å –æ—Å–æ–∑–Ω–∞–Ω–∏–µ–º. –ù–∞ –≤–¥–æ—Ö–µ –¥—É–º–∞–π—Ç–µ "—è –≤–¥—ã—Ö–∞—é", –Ω–∞ –≤—ã–¥–æ—Ö–µ ‚Äî "—è –≤—ã–¥—ã—Ö–∞—é". –°–ª–µ–¥–∏—Ç–µ –∑–∞ —Ç–µ–º, –∫–∞–∫ –≤–æ–∑–¥—É—Ö –¥–≤–∏–∂–µ—Ç—Å—è –≤ –≤–∞—à–µ–º —Ç–µ–ª–µ.\n5. –ú–µ–¥–∏—Ç–∞—Ü–∏—è —Å –Ω–∞–º–µ—Ä–µ–Ω–∏–µ–º. –ü–æ—Å—Ç–∞–≤—å—Ç–µ –ø–µ—Ä–µ–¥ —Å–æ–±–æ–π –Ω–∞–º–µ—Ä–µ–Ω–∏–µ –Ω–∞ –º–µ–¥–∏—Ç–∞—Ü–∏—é ‚Äî —ç—Ç–æ –º–æ–∂–µ—Ç –±—ã—Ç—å –≤–Ω—É—Ç—Ä–µ–Ω–Ω–∏–π –≤–æ–ø—Ä–æ—Å –∏–ª–∏ –Ω–∞–º–µ—Ä–µ–Ω–∏–µ: "–ß—Ç–æ —è —Ö–æ—á—É –ø–æ–Ω—è—Ç—å?" –∏–ª–∏ "–ö–∞–∫ —è –º–æ–≥—É –±—ã—Ç—å –±–æ–ª–µ–µ —Å–ø–æ–∫–æ–π–Ω—ã–º?". –î–∞–π—Ç–µ —Å–µ–±–µ –≤—Ä–µ–º—è, —á—Ç–æ–±—ã —ç—Ç–æ –Ω–∞–º–µ—Ä–µ–Ω–∏–µ —Å—Ñ–æ—Ä–º–∏—Ä–æ–≤–∞–ª–æ—Å—å.\n6. –†–∞—Å—Å–ª–∞–±–ª–µ–Ω–∏–µ –∫–∞–∂–¥–æ–π —á–∞—Å—Ç–∏ —Ç–µ–ª–∞. –ù–∞—á–Ω–∏—Ç–µ —Å –≥–æ–ª–æ–≤—ã –∏ –ø–æ—Å—Ç–µ–ø–µ–Ω–Ω–æ –¥–≤–∏–≥–∞–π—Ç–µ—Å—å –≤–Ω–∏–∑. –ö–æ–≥–¥–∞ –≤—ã –æ—Å–æ–∑–Ω–∞–µ—Ç–µ –∫–∞–∂–¥—ã–π —É—á–∞—Å—Ç–æ–∫ —Å–≤–æ–µ–≥–æ —Ç–µ–ª–∞, –º—ã—Å–ª–µ–Ω–Ω–æ –ø–æ—Å—ã–ª–∞–π—Ç–µ —Ç—É–¥–∞ —Ä–∞—Å—Å–ª–∞–±–ª–µ–Ω–∏–µ.\n7. –í–∏–∑—É–∞–ª–∏–∑–∞—Ü–∏—è. –ü—Ä–µ–¥—Å—Ç–∞–≤—å—Ç–µ, —á—Ç–æ –≤—ã –Ω–∞—Ö–æ–¥–∏—Ç–µ—Å—å –≤ –º–µ—Å—Ç–µ –ø–æ–∫–æ—è –∏ —Å–ø–æ–∫–æ–π—Å—Ç–≤–∏—è ‚Äî –Ω–∞–ø—Ä–∏–º–µ—Ä, –≤ —Ç—É–º–∞–Ω–Ω–æ–º –ª–µ—Å—É, –Ω–∞ –ø–ª—è–∂–µ –∏–ª–∏ –≤ –≥–æ—Ä–∞—Ö. –í–¥–æ—Ö–Ω–∏—Ç–µ –∞—Ç–º–æ—Å—Ñ–µ—Ä—É —ç—Ç–æ–≥–æ –º–µ—Å—Ç–∞ –∏ –ø–æ–∑–≤–æ–ª—å—Ç–µ —Å–µ–±–µ –ø–æ–≥—Ä—É–∑–∏—Ç—å—Å—è –≤ –Ω–µ–≥–æ.\n8. –û—Å–æ–∑–Ω–∞–Ω–∏–µ –º—ã—Å–ª–µ–π. –ï—Å–ª–∏ –≤–∞—à–∏ –º—ã—Å–ª–∏ –æ—Ç–≤–ª–µ–∫–∞—é—Ç –≤–∞—Å, –ø—Ä–∏–º–∏—Ç–µ –∏—Ö –±–µ–∑ –æ—Å—É–∂–¥–µ–Ω–∏—è. –°–∫–∞–∂–∏—Ç–µ —Å–µ–±–µ: "–Ø –Ω–∞–±–ª—é–¥–∞—é —ç—Ç–∏ –º—ã—Å–ª–∏, –Ω–æ –Ω–µ –ø–æ–∑–≤–æ–ª—è—é –∏–º –æ—Ç–≤–ª–µ–∫–∞—Ç—å –º–µ–Ω—è –æ—Ç –Ω–∞—Å—Ç–æ—è—â–µ–≥–æ –º–æ–º–µ–Ω—Ç–∞".\n9. –ü—Ä–∏–Ω—è—Ç–∏–µ —Ç–µ–∫—É—â–µ–≥–æ —Å–æ—Å—Ç–æ—è–Ω–∏—è. –ü—Ä–∏–∑–Ω–∞–π—Ç–µ –∏ –ø—Ä–∏–º–∏—Ç–µ –≤—Å–µ —Å–≤–æ–∏ —ç–º–æ—Ü–∏–∏, –æ—â—É—â–µ–Ω–∏—è, –º—ã—Å–ª–∏, –Ω–µ –ø—ã—Ç–∞—è—Å—å –∏—Ö –∏–∑–º–µ–Ω–∏—Ç—å. –ü—Ä–æ—Å—Ç–æ –Ω–∞–±–ª—é–¥–∞–π—Ç–µ.\n10. –ó–∞–∫–ª—é—á–µ–Ω–∏–µ. –ü–ª–∞–≤–Ω–æ –∑–∞–∫–∞–Ω—á–∏–≤–∞–π—Ç–µ –º–µ–¥–∏—Ç–∞—Ü–∏—é, –≤–æ–∑–≤—Ä–∞—â–∞—è –≤–Ω–∏–º–∞–Ω–∏–µ –∫ –¥—ã—Ö–∞–Ω–∏—é. –ú–µ–¥–ª–µ–Ω–Ω–æ –æ—Ç–∫—Ä–æ–π—Ç–µ –≥–ª–∞–∑–∞, –ø–æ—Ç—è–Ω–∏—Ç–µ—Å—å –∏ –≤–µ—Ä–Ω–∏—Ç–µ—Å—å –≤ –æ—Å–æ–∑–Ω–∞–Ω–Ω–æ–µ —Å–æ—Å—Ç–æ—è–Ω–∏–µ, —Å–æ—Ö—Ä–∞–Ω—è—è —á—É–≤—Å—Ç–≤–æ –ø–æ–∫–æ—è.\n\n–ü–æ–¥—Å–∫–∞–∑–∫–∞: –≠—Ç–∞ –º–µ–¥–∏—Ç–∞—Ü–∏—è –∏–¥–µ–∞–ª—å–Ω–æ –ø–æ–¥—Ö–æ–¥–∏—Ç –¥–ª—è –≥–ª—É–±–æ–∫–æ–≥–æ —Ä–∞—Å—Å–ª–∞–±–ª–µ–Ω–∏—è, —Å–Ω—è—Ç–∏—è —Å—Ç—Ä–µ—Å—Å–∞ –∏ –æ–±—Ä–µ—Ç–µ–Ω–∏—è –≤–Ω—É—Ç—Ä–µ–Ω–Ω–µ–π –≥–∞—Ä–º–æ–Ω–∏–∏.')

