@dp.message(State("waiting_for_goal_description"))
async def process_goal_description(message: Message, state: FSMContext):
    description = message.text
    await state.update_data(description=description)

    # –ü–µ—Ä–µ—Ö–æ–¥–∏–º –∫ –≤–≤–æ–¥—É –¥–∞—Ç—ã –∑–∞–≤–µ—Ä—à–µ–Ω–∏—è
    await message.answer("–í–≤–µ–¥–∏—Ç–µ –¥–∞—Ç—É –∑–∞–≤–µ—Ä—à–µ–Ω–∏—è —Ü–µ–ª–∏ (–Ω–∞–ø—Ä–∏–º–µ—Ä, 2025-12-31), –∏–ª–∏ –Ω–∞–ø–∏—à–∏—Ç–µ '–±–µ—Å—Å—Ä–æ—á–Ω–æ':")
